style.css:
@-moz-document url-prefix("https://onepieceex.net/") {
body {
    margin: 0;
    font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #d9d9d9 !important;
    text-align: left;
    background-color: #2a2a2a;
}

#base {background: url(https://i.imgur.com/byBfdgD.png) center top repeat-x ;}

#aviso span {
    text-shadow: 2px 0px 0px rgb(0 0 0);
    background: #1c1c1c;
}

#noticias article a, .post-message  {
    color: #d9d9d9 !important;
}

#noticias article p:before {
    content: '';
    width: 100%;
    height: 40px;
    position: absolute;
    bottom: 0;
    display: block;
    background: -moz-linear-gradient(top, #2a2a2a 0%, #1c1c1c 100%);
    background: -webkit-linear-gradient(top,#2a2a2a 0%,#1c1c1c 100%);
    background: linear-gradient(to bottom,#2a2a2a 0%,#1c1c1c 100%);
    z-index: -1;
}

#conteudo article#post {
    width: 840px;
    float: left;
    margin-top: 12px;
    clear: both;
    background: #2a2a2a;
    z-index: 2;
}

.titulo.caps span {
    color: var(--cor-media);
    z-index: 100;
    background: #2a2a2a;
    padding-right: .5em;
    display: inline-block;
}

.bloco-destaque, #infoPlusUltra.midia{background: #1c1c1c !important;}

.sombra-branca, .sombra-branca-30 {
    text-shadow: 2px 0px 0px rgb(0 0 0 / 60%);
}

#reactions, .text-bold.align.align--center {color: #b9b9b9 !important;}

.noticia-refer .comentario .texto {
    color: #b9b9b9;
    font-weight: normal;
    z-index: 10;
    position: relative;
    padding: 5px 10px;
    width: 110%;
    height: 200px;
    background: -moz-linear-gradient(left, #1c1c1c 0%, #2a2a2a 100%);
    background: -webkit-linear-gradient(left,#1c1c1c 0%,#2a2a2a 100%);
    background: linear-gradient(to right,#1c1c1c 0%,#2a2a2a 100%);
}

.imagem-texto:before {
    content: '';
    width: calc(100% + 10px);
    height: calc(100% + 10px);
    top: -10px;
    left: -10px;
    position: absolute;
    bottom: 0;
    display: block;
    z-index: 15;
    background: -moz-radial-gradient(center,ellipse cover,#1c1c1c 12%, #2a2a2a 100%);
    background: -webkit-radial-gradient(center,ellipse cover,#1c1c1c 12%,#2a2a2a 100%);
    background: radial-gradient(ellipse at center,#1c1c1c 12%,#2a2a2a 100%);
}

.curiosidades-mais, .volume .capitulos {background: #1c1c1c !important;}

.degra-base-branco {
    display: block;
    background: -moz-linear-gradient(top,#2a2a2a 0%, #1c1c1c 100%);
    background: -webkit-linear-gradient(top,#2a2a2a 0%,1c1c1c 100%);
    background: linear-gradient(to bottom,#2a2a2a 0%,#1c1c1c 100%);
}

.wp-playlist-light .wp-playlist-playing {
    background: #1c1c1c;
    color: #d9d9d9;
}

.wp-playlist-item-artist {color: var(--cor-clara-80);}

.wp-playlist {border: 1px solid var(--cor-media-80);}

.wp-playlist-light {
    background: #2a2a2a;
    color: #b9b9b9;
}

#lista-indicacoes li strong, #lista-indicacoes li i {color: #d9d9d9;}

div.wpforms-container-full .wpforms-form input[type=submit], div.wpforms-container-full .wpforms-form button[type=submit], div.wpforms-container-full .wpforms-form .wpforms-page-button{
    background-color: var(--cor-media);
    border: 0px solid transparent;
    color: white;
}

div.wpforms-container-full .wpforms-form input[type="submit"]:hover, div.wpforms-container-full .wpforms-form input[type="submit"]:active, div.wpforms-container-full .wpforms-form button[type="submit"]:hover, div.wpforms-container-full .wpforms-form button[type="submit"]:active, div.wpforms-container-full .wpforms-form .wpforms-page-button:hover, div.wpforms-container-full .wpforms-form .wpforms-page-button:active {
    background-color: var(--cor-clara);
    border: 0px solid transparent;
}

div.wpforms-container-full .wpforms-form input[type=date], div.wpforms-container-full .wpforms-form input[type=datetime], div.wpforms-container-full .wpforms-form input[type=datetime-local], div.wpforms-container-full .wpforms-form input[type=email], div.wpforms-container-full .wpforms-form input[type=month], div.wpforms-container-full .wpforms-form input[type=number], div.wpforms-container-full .wpforms-form input[type=password], div.wpforms-container-full .wpforms-form input[type=range], div.wpforms-container-full .wpforms-form input[type=search], div.wpforms-container-full .wpforms-form input[type=tel], div.wpforms-container-full .wpforms-form input[type=text], div.wpforms-container-full .wpforms-form input[type=time], div.wpforms-container-full .wpforms-form input[type=url], div.wpforms-container-full .wpforms-form input[type=week], div.wpforms-container-full .wpforms-form select, div.wpforms-container-full .wpforms-form textarea {
    color: #b9b9b9;
    background: #1c1c1c;
}

#aniversarios-lista li ul li, #personagens-lista li {
    background: -moz-linear-gradient(top, #1c1c1c 0%, #2a2a2a 100%);
    background: -webkit-linear-gradient(top, #1c1c1c 0%, #2a2a2a 100%);
    background: linear-gradient(to bottom, #1c1c1c 0%,#2a2a2a 100%);
}

#aniversarios-lista li ul li:hover, #personagens-lista li:hover {background: #1c1c1c;}

#conteudo a:hover, .link-padrao a:hover, #fim a:hover{color: var(--cor-clara);}
}
