/*
 * Google Web Search - The Google bay theme
 *
 * Author      : mandel99
 * Updated     : August 27th, 2009
 * Description : Modifies the images on Google Web Search (http://www.google.com/ and other language versions of google)
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document  url-prefix("http://www.google"),
                url-prefix("http://www.google.com")
{ 
img[src*="logos/"], 
img[src$="logo.gif"], 
div[style*="logo_plain.png"], 
img[src$="logo_google_suggest.gif"], 
img[src^="logos/holiday"], 
img[src^="/intl/"][alt="The Google bay"][title="The Google Bay"] 
{
  width: 0px !important;
  height: 295px !important;
  padding-left: 0px !important;
  padding-right: 275px !important;
  padding-top: 30px !important;
  margin-bottom: -45px !important;
  background-image: url("http://img385.imageshack.us/img385/6571/tgb.jpg") !important;
}
}
