@-moz-document url-prefix("https://www.eldarya.") {
/*Dziękuję Driada za podrzucenie kodu!*/


/*WYGLĄD STRONY*/
/*wykopanie Purrala xD*/
#purrotroc-left-frame
{
  display: none;
}
/*położenie i rozmiar koła*/
#purrotroc-right-frame
{
  top: -5px;
  left: 360px;
  width: 450px;
  height: 450px;
}
/*zmiana miejsca położenia 2, 3, 4 i 5 itemu na kole*/
#need-content .purrotroc-item:nth-child(2)
{
  left: -80px;
}

#need-content .purrotroc-item:nth-child(3)
{
  bottom: 60px;
  right: -60px;
}

#need-content .purrotroc-item:nth-child(4)
{
  top: -60px;
  left: 30px;
}

#need-content .purrotroc-item:nth-child(5)
{
  bottom: -30px;
  left: 120px;
}
/*położenie sakwy w kole*/
#bag-container
{
  top: 120px;
}
/*położenie sakwy i przycisków przy odbieraniu sakwy*/
#bag-container.opening
{
  top: -30px;
}
/*położenie i szerokość pola nazwy i kategorii itemu*/
.need-label
{
  width: 220px;
  left: -54px;
}
/*wygląd i położenie tekstu*/
.need-preview img + span::after
{
  position: absolute;
  top: 147px;
  left: -197px;
  font-size: 15px;
  width: 350px;
  color: white;
  font-weight: bold;
  line-height: 15px;
  font-family: 'Alegreya Sans SC', sans-serif;
  text-shadow: 0 0 10px #000;
}










