@-moz-document domain("brainyquote.com") {
/* Font Settings */

a[href],
td span
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.pagination>.active>span,
.pagination>.active>span:hover,
.pagination>.active>span:focus,
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.active>a:hover,
.navbar-default .navbar-nav>.active>a:focus,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

.pagination>.disabled>span,
.pagination>.disabled>span:hover,
.pagination>.disabled>span:focus,
.pagination>.disabled>a,
.pagination>.disabled>a:hover,
.pagination>.disabled>a:focus,
.qll-new .b-qt,

body,
li,
p
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

.dropdown-header,

[class*='qa_'],
h1,
h2
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

.homeBirthdays .bqLn,
time
 {
	color: #07beff!important;
}

/*__________________________________________________________________________________*/

input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f;
	opacity: 1;
}

}

@-moz-document domain("brainyquote.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.bq-bluebar,
.copyLangFooter,
.quoteDetailBody .quotesToExploreHeader
{
	background-color: #060505!important;
}

.bq-navbar,
.bq_bot_nav,
.navbar-default
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.homeResponsive,
.bq-search .s-btn,
.fbIconBlock,
.footer-follow-icons li,
.instaIconBlock,
.pagination>.disabled>a,
.pagination>.disabled>a:focus,
.pagination>.disabled>a:hover,
.pagination>.disabled>span,
.pagination>.disabled>span:focus,
.pagination>.disabled>span:hover,
.pagination>li>a,
.pagination>li>span,
.qll-new .kw-box a,
.qll-new .kw-box,
.qotdDayHeaderBar,
.qti-listm,
.twIconBlock
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-primary
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-primary::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-primary:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.boxy,
.container-fluid,
.homeResponsive .container.bqTopLevel,
.pagination,
.qb,
.qll-new.qotdBody .mblCenterPhot,

main
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.dropdown-menu,
.block-dark-grey,
.block-light-grey,
.block-white,
.block-sm,
.homeGridBox
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.block-dark-grey:hover,
.block-light-grey:hover,
.block-sm:hover,
.block-white:hover,
.boxy:hover,
.container-fluid:hover,
.dropdown-menu:hover,
.homeGridBox:hover,
.homeResponsive .container.bqTopLevel:hover,
.pagination:hover,
.qb:hover,
.qll-new.qotdBody .mblCenterPhot:hover,

main:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.bq-tnav.navbar .bq-bluebar .nav>.active>a,
.bq-tnav.navbar .bq-bluebar .nav>.active>a:focus,
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover,
.pagination>li>a:hover,
.pagination>li>span:hover
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}



}

