style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* laacz.lv izsmucinaashana */
@-moz-document domain("laacz.lv") {
	body {
	color: #000 !important;
	font-size: 0.9em !important;
	}
	#commentslist {
	font-size: 0.9em !important;
	}
	#commentslist li {
	border: 1px solid #E7B551 !important;
	}
	#commentslist li.alt {
	background: #fdfaf5 !important;
	}
	#commentslist li:hover {
        border-color: #91000e !important;
	}
	#commentslist li:hover .posted {
	font-weight: bold;
	letter-spacing: 0.1em;
	}
}
