/* ==UserStyle==
@name         Minimal Tumblr
@version      20210907.11.21
@namespace    userstyles.world/user/amkitakk
@description  Hides the right sidebar and centers the dashboard.
@author       amkitakk
@license      No License
==/UserStyle== */

@-moz-document url-prefix("https://www.tumblr.com/") {
/*hide sidebar*/
.u8YQA,
#qc-cmp2-container,
aside,
.e1knl {
	display: none
}
/*center dashboard*/
.T5uZR {
	margin: auto;
}
.gPQR5 {
	width: max-content;
}
/*hide promoted*/
.g8sNN > .IGdYk {
	display: none;
}
/*hide recommended*/
._ekIx,
.ODcQ6._g0_4 {
	display: none;
}
/*hide explore*/
div.kIuBF:nth-of-type(2) {
	display: none;
}
/*hide ad on top of dashboard*/
.moatContainer.njwip {
	display: none;
}
}
