style.css:
@-moz-document url-prefix("https://www.roblox.com"), url-prefix("https://web.roblox.com") {
/* VARIABLE. NOT MY FORMATING/CODING, I AM SIMPLY CHANGING INPUTS!! */
:root {
    --darkpurple: rgb(29, 15, 77);
    --darkerpurple: rgb(19, 9, 51);
    --regularpurple: rgb(75, 52, 153);
    --purple: rgb(48, 23, 130);
    --green: rgb(153, 209, 185);
    --red: rgb(237, 92, 92);
    --vibrantpurple: rgb(79, 39, 217);
    --brightestpurple: rgb(124, 88, 245);
    --white: rgb(255,255,255);
    --black: rgb(0,0,0);
}


.robux-table, #footer-container, #header, #chat-main, table.table.summary, .people-list, .section-content, .menu-option-content, .group-tab, #notification-stream-body, .slide-item-container-left, .modal-content, .rbx-tab-heading, #horizontal-tabs, .recommended-items, #search-options, .nav-tabs, #MessagesInbox, .roblox-message-row, #vertical-menu, .rbx-vip-server-item, .create-server-banner, #Notifications, .message-news, #groups-configure-vertical-menu, .audit-log, #mCSB_3_container, .rbx-friends-game-server-item, .badge-row, #rbx-gear-container, #MyTransactions_tab, #TimePeriod, #TradeItems_TradeType, .SquareTabGray, #MyCreationsTabLink, #GroupCreationsTabLink, #LibraryTabLink, #DevExTabLink, #PayoutTabLink, #dropdownUl, .dialog-body, #mCSB_10, .popover, .details-dialog-container, .conversation-title-editor-body, .tab-container div, #navbar, .blank-box, #configure-localization-tabs-navigation, .details-negative-confirmation-body, .message-reply, .select-friends-search, .conversation-title-container, .game-update-input-text, .default-error-page, a.text-link, a-text-link.ng-binding, .GenericModalBody, .btr-game-playbutton, .btn-primary-md, .group-card  {
    background-color: var(--darkpurple) !important;
}

.games-list-container, .avatar-cards, #LibraryTab, #DeveloperExchangeTab, #PayoutTab, .BuildPageContent, #templates_tab, #basicsettings_tab, #access_tab, #advancedsettings_tabm, #universe-configure, #placeForm, #AssetCreationContainer, .game-card-container, #game, #payout, #data, .store-card, #rbx-game-server-item-container, .rbx-vip-server-item-container, .item-card-container, .group-details .group-wall, #feed-container .section-content, .premium-card, .perks, .stack-list .stack-row, .money-page-banner, .trades-list-detail .col-xs-12, #trade-row-scroll-container .col-xs-12 {
    background-color: var(--darkpurple) !important;
}

.roblox-message-row, .message-news, .notification-stream-item {
    border-color: var(--vibrantpurple) !important;
}

#chat-header, .dropdown-menu, .notification-stream-item, .menu-secondary-option, .menu-secondary, .table-header th, .table-header th, #build-dropdown-menu, #build-page td.menu-area a.tab-item-selected, .CatalogHoverContent, .CatalogItemInner, .add-friends-option, .friends-list-container, .tooltip {
    background-color: var(--darkpurple) !important;
}

#navigation, .section-content-off, .notification-stream-header, .profile-avatar-mask, .slide-item-container-right, .selected, .chat-windows-header, .dialog-input-container, .dialog-input, .missing-start-place, #DeveloperProductStatus, .premium-card-top, .select-friends-btns, .mCustomScrollBox, .toggle-three-dee, .conversation-title-input, #chat-friends {
    background-color: var(--purple) !important;
}

.notification-blue, .notification-red, .limited-icon-container, .PurchaseButton {
    background-color: var(--regularpurple) !important;
    color: var(--white) !important;
}

.accoutrement-image, .asset-thumb-container, .item-card-thumb-container, .SquareTabsContainer .selected, #DevelopTabs .tab-active, .selectedAssetTypeFilter, .tab-container .active, .scroller {
    background-color: var(--regularpurple) !important;
}

#postButton, .group-form-button, .create-group-button, .group-cancel, #shareButton, .profile-view-selector, .header-userstatus-share-button, #upgrade-now-button, .modal-button, #save-social-settings, #SaveInfoSettings, .load-more-button, .rbx-post-comment, .input-dropdown-btn, .roblox-archiveButton, .roblox-markAsReadInbox, .roblox-markAsUnreadInbox, .roblox-moveToInboxButton, .rbx-vip-server-create, .rbx-game-server-join, .rbx-running-games-load-more, .see-more-resellers, .message-detail-mark-archive, #roblox-reply-btn, .ignore-friend, #roblox-archive-btn, .decline-requests-button, .decline-request-button, #group-join-button, .rbx-friends-game-server-join, #MyTransactions_TransactionTypeSelect, #SelectedGroupId, .place-creationcontext-drop-down, .universe-selection-drop-down, #purchase-amount-message, #SortMain, #categoriesForKeyword, #keywordTextbox, #creatorTextbox, #promote-gear-btn, #select-friends-cancel, #leave-group, #Genre, #MaxPlayersInput, #Access, #FriendSlotsInput, .back-button, .preview-button, #decline-btn, .remove-button, .social-link-type-dropdown, .input-field, #submit-button, #selenium-manage-translations-page-link, .place-btn, #report-abuse, #costumes .btn-float-right, #blockedUsersToggle, .robux-button-container .btn-control-md, .avatar-editor-header .catalog-header .btn-primary-md, .roblox-sendMessage, .enable-three-deel, .revertLink, .flush-button, .default-error-page .action-buttons .btn-primary-md, .PlaceSelectorModal {
    color: var(--white) !important;
    border-color: var(--brightestpurple) !important;
    background-color: var(--purple) !important;
}
.rbx-vip-server-renew, .accept-friend, .accept-requests-button, .accept-request-button, #select-friends-save, .InstallButton, .blocked-users-btn {
    color: var(--white) !important;
    border-color: var(--brightestpurple) !important;
    background-color: var(--darkpurple) !important;
}
.more-robux-button .btn-control-md, .profile-join-game, .add-button, .acct-upgrade-membership, #confirm-btn {
    color: var(--white) !important;
    border-color: var(--darkpurple) !important;
    background-color: var(--purple) !important;
}
.vote-percentage {
    background-color: var(--green) !important;
}
.vote-background {
    background-color: var(--red) !important;
    
}   
.PurchaseButton {
background-color: var(--purple) !important;
border-color: var(--brightestpurple) !important;   
    
}

/* TRANSPARENT BGS*/
body, .content, .thumbnail-2d-container, .profile-collections, .recommendations-container, .game-card-thumb-container, .game-cards-grid, #GamesListsContainer, .games-filter-changer, .games-list-container, .avatar-card-container, .avatar-card-btns, .menu-option-content, #Body, #RepositionBody, .RobuxColumn, #TabsContentContainer, #BodyWrapper, #MyCreationsTab, #GroupCreationsTab, .gear-button-wrapper, .SmallCatalogItemView, .profile-container .tab-content, .rbx-game-server-item, .rbx-vip-server-item, .tooltip-inner, #advancedsettings_tab {
    background: transparent !important;
}

.category-section, .search-options-form, .content-area, .gear-button-wrapper {
    border: none !important;
}

/* SOME TEXT COLORING */
.legal-text-holder, .ng-binding, .container-header h3, #badge-container h1, #badge-container h2 {
    color: var(--white) !important;
    text-shadow:none, 1px 1px 2px var(--darkpurple);
}
#accept-fr-btn {
    color: var(--darkerpurple) !important;
}
.text-label, a, .form-label, td, #TimePeriod, #TradeItems_TradeType, .light span, .table-header, #DevelopTabs div, #CashOutWidget h2, #CashOutWidget h3, #CashOutWidget div, #DevExLeftColumn, #DevExLeftColumn h2, #PayoutTab, #PayoutTab h2, .content-title h2, .content-title label, #build-page td.menu-area a.tab-item, .content-area h2, #container label, #container div, #catalog .left-nav-menu, #catalog .left-nav-menu h2, .CatalogHoverContent span, #templates_tab h2, #templates_tab p, #basicsettings_tab h2, #access_tab h2, #access_tab, #advancedsettings_tab h2, #advancedsettings_tab, .tab-container, #Body h1, #Body h2, #Body, #Body h3, #modal-dialog, .social-link-type-dropdown, .page, #AssetCreationContainer, .text, .footnotel, .premium-card-body h2 span, h1, .container-fluid span, .container-fluid div, p, h2, div, span, .messages-reply-box, .game-update-input-text {
    color: var(--white) !important;
}
.text-favorite {
    color: var(--white) !important;
}
.abuse-report-link .text-error, .text-report, #vote-down-text, #FriendSlotWarning, .root-place span {
    color: var(--red) !important;
}
.vote-percentage-label, #DevExLeftColumn a, .totals-label span, #advancedsettings_tab a, .footnote a, .membership-info a, .text-link, .game-name-text, .group-card-members span {
    color: var(--brightestpurple) !important;
}
.roblox-edit-button, #submitSearchButton, #submitCreatorButton, .revertLink, .morerecords .btn-control {
    color: var(--black) !important;
}
#nav-robux-amount, .text-robux-tile, .text-robux-lg, .text-robux, #vote-up-text, .money, .amount-value, .NotAPrice, .affiliate-sale-total, #PrivateServerMarketplaceFeeText, #PrivateServerUserProfitText, .premium-card-body h2 {
    color: var(--white) !important;
}

/* BG IMAGE */
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://i.imgur.com/SbzphPc.jpg") !important; /* idk where the image is from but credits to the artist <3 */
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(4px) !important; 
}
}
