style.css:
@-moz-document domain("youtube.com") {
html[watch-color-update] {
	background: var(--yadbg) /*[[yadbgchat]]*/!important;
	text-shadow: 0px 0px 4px #000!important;
	background-blend-mode: multiply!important;
	}

	body {
	color: var(--mt);
	background: transparent;
	overflow: overlay
	}
	::-moz-selection {
	color: var(--ytcp-text-primary);
	background: var(--caretbg);
	}
	::selection {
	color: var(--ytcp-text-primary);
	background: var(--caretbg);
	}

	#buttons.ytd-masthead:after {
	content: "/*[[myname]]*/";
	font-family: Bungeespice;
	font-size: 25px;
	padding-top: 2%;
	max-height: 30px!important;
	color: var(--yadfc4);
	}

	#buttons.ytd-masthead:after {
	animation: slide-top 0.5s 2s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
	}

	@keyframes slide-top {
	0% {transform: translateY(-100px);}
	99.9% {text-shadow: 0px 0px 6px var(--ml)}
	}

	#contentWrapper.tp-yt-iron-dropdown > * {
	overflow: hidden;
	animation: yadpred3 0.1s 1
	}
	@keyframes yadpred3 {
	0% {
	-webkit-transform: rotateX(80deg);
	transform: rotateX(80deg);
	opacity: 0;
	}
	100% {
	-webkit-transform: rotateX(0);
	transform: rotateX(0);
	opacity: 1;
	}
	}
	
	/*header*/
    #container.ytd-masthead::after {
		content: "";
		width: 100%;
		height: 100%;
		background: linear-gradient(0deg, #0000 0%, var(--yadtheme) 304%), url(/*[[yadbg]]*/) var(--bg) top center/cover no-repeat;
		position: absolute;
		display: block;
		z-index: -4;
		left: 0;
		top: 0;
    	box-shadow: 0 3px 0px 1px var(--hd2);
}	
	/*letsgo*/
	body {
		overflow-x: hidden!important
	}
	
	ytd-app {
		z-index: -11;
		background: var(--bg)
	}
	ytd-app::before {
		content: '';
		position: fixed;
		width: 100%;
		height: 100%;
		z-index: -10;
		background: linear-gradient(180deg, #0000 70%, var(--yadtheme))
	}
	ytd-app::after {
		content: "";
		width: 100%;
		height: 100%;
		background: linear-gradient(180deg, #0000 70%, var(--yadtheme)), var(--yadbg) var(--yadbgs);
		position: fixed;
		display: block;
		z-index: -3;
		top: 0;
		bottom:0;
	}
	#guide-content.ytd-app::after {
		content: "";
		width: 100%;
		height: 100%;
		background: linear-gradient(180deg, #0000 70%, var(--yadtheme)), var(--yadbg) var(--wallpas2);
		position: fixed;
    display: block;
		z-index: -2;

		top: 0;
		left: 0;
		bottom:0;
	}
	#guide-content.ytd-app {
		background: transparent;
	}
		
		
		
		
	

	/*logo*/
	[d*="M27.9727 3.12324C27.6435 1.89323 26"] {
	fill: var(--yadtheme);
	filter: brightness(1.3);
	stroke: var(--yadfc4);
	stroke-opacity: 30%
	}
	[d*="M11.4253 14.2854L18.8477 10.0004L11.4253"] {
	stroke: var(--yadfc4);
	stroke-width: 1;
	stroke-opacity: 30%
	}


	ytd-active-account-header-renderer {
	background-color: #21212100;
	border-bottom: 1px solid #21212100;
	}
	ytd-simple-menu-header-renderer,
	ytd-simple-menu-header-renderer[darker-dark-theme]{
	background-color: #51202000;
	border-bottom: 0px solid #00000000;
	}
	yt-icon.ytd-topbar-logo-renderer {
	color: var(--yadfc4)
	}

	yt-icon.ytd-logo,
	#country-code.ytd-topbar-logo-renderer {
	color:  /*[[topicons]]*/
	}

	/*video grid*/
	#primary.ytd-two-column-browse-results-renderer {
	background-color: #36384100;
	background-image: none;
	}
	/*active page*/
	ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer,
	ytd-mini-guide-entry-renderer[active] .guide-icon.ytd-mini-guide-entry-renderer,
	ytd-mini-guide-entry-renderer[active] .title.ytd-mini-guide-entry-renderer {
	color: var(--ytcp-themed-red)
	}
	/*originals title*/
	.badge-style-type-collection.ytd-badge-supported-renderer {
	color: #cccaca	
	}
	/*undo etc*/
	[class="style-scope ytd-button-renderer style-blue-text"],
	ytd-button-renderer.style-blue-text[is-paper-button],
	ytd-button-renderer.style-blue-text[is-icon-button] {
	color: var(--ml)
	}
	/*add*/
	ytd-thumbnail-overlay-toggle-button-renderer[use-expandable-tooltip][toggled] yt-icon.ytd-thumbnail-overlay-toggle-button-renderer, ytd-thumbnail-overlay-toggle-button-renderer[use-expandable-tooltip][toggled] #label.ytd-thumbnail-overlay-toggle-button-renderer {
	color: var(--ml)
	}

	/*banner*/
	div#content-wrapper.style-scope.ytd-statement-banner-renderer,
	.badge-style-type-featured.ytd-badge-supported-renderer,
	#action-button.ytd-primetime-promo-panel-renderer,
	ytd-button-renderer.style-suggested-action[is-paper-button],
	.style-scope ytd-rich-metadata-renderer,
	#contents.yt-live-chat-mode-change-message-renderer,
	#card.yt-live-chat-viewer-engagement-message-renderer,
	#star-survey.ytd-inline-survey-renderer,
	ytd-inline-survey-renderer[expanded] #dismissible.ytd-inline-survey-renderer,
	#dismiss-button.ytd-inline-survey-renderer
	{
	background: var(--yt)!important;
	text-shadow: 0 0;
	color: #fff;
	box-shadow: 0px 0px 0px 1px var(--yadtheme);
	border-radius: 10px;
	overflow: hidden
	}
	.sbpqs_a:before,.sbqs_c:before,
	[src*="/growth/b84bef6e4741df6a265"]
	{
	filter: invert(1)
	}
	[aria-label="Try it free"],[style="color: rgb(0, 0, 0);"],
	ytd-rating-survey-option-renderer,
	[class="style-scope ytd-button-renderer style-blue-text size-default"][aria-label="Not sure"] {
	color: var(--ml)!important
	}
	.ytd-mealbar-promo-renderer-message-text.ytd-mealbar-promo-renderer {
	color: #ffffff7a	
	}
	[class="style-scope yt-formatted-string"][style="color: rgb(33, 33, 33);"],
	[class="style-scope yt-formatted-string"][style="color: rgb(40, 40, 40);"],
	[style="color: rgb(19, 19, 19);"], #badge,
	#title.ytd-inline-survey-renderer, #follow-up-title.ytd-inline-survey-renderer,
	#subtitle.ytd-inline-survey-renderer,
	#selected-response-text.ytd-rating-survey-renderer {
	color: #fff!important	
	}
	[src*="14e2b1d119413d7d235541b63df1b100022ab953b70d425f4521e1a331bf7c44_214x48.png"] {
	filter: invert(1) brightness(1) saturate(1%)
	}
	[d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"] {
	fill: var(--yadfc2)
	}
	ytd-checkbox-survey-option-renderer.ytd-inline-survey-renderer {
	--paper-checkbox-unchecked-color: #fff;
	--paper-checkbox-label-color: var(--ml);
	background-color: #ffebeb00;
	}
	#inline-survey-compact-video-renderer.ytd-inline-survey-renderer {
	background: var(--yadbg) #0000003f;
	background-blend-mode: darken;
	text-shadow: 0px 0px 0px #0b0b0be6;	
	}

	/*brrrrr*/
	html:not(.style-scope) {
	--yt-spec-static-brand-red: var(--ml)!important
	}
	 

	ytd-app:not([background-color-update_]) app-drawer.ytd-app:not([persistent]).ytd-app #header.ytd-app {
	border-bottom: 1px solid #0000;
	}
	#info-strings.ytd-video-primary-info-renderer {
	color: var(--yt-spec-text-secondary)	
	}

	/*skeletons*/

	ytd-masthead.dark .masthead-skeleton-icon,
	.masthead-skeleton-icon,
	html[dark] .watch-skeleton .skeleton-bg-color,
	.watch-skeleton .skeleton-bg-color {
	background: linear-gradient(180deg, #0000 0%, var(--yadtheme) 600%);
	box-shadow: 0px 0px 0px 1px var(--yadtheme)
	}


	/*side panel*/ 
	ytd-app[darker-dark-theme] #guide-content.ytd-app,
	ytd-mini-guide-renderer[darker-dark-theme]
	#guide-content.ytd-app,
	ytd-mini-guide-renderer,
	ytd-settings-sidebar-renderer {
	background: #fff0;
	}
	ytd-mini-guide-entry-renderer {
	background: #0000
	}
	.guide-icon.ytd-guide-entry-renderer {
	filter: drop-shadow(0px 0px 0px var(--yadtheme))
	}
	ytd-compact-link-renderer[compact-link-style=compact-link-style-type-settings-sidebar][active] {
	background: var(--ml)14
	}
	#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus {
	background: #ffffff0a;
	}
	#guide-wrapper.ytd-app {
	background-color: #fff0;
	}
	tp-yt-paper-item.ytd-guide-entry-renderer:hover {
	background: #fff0;
	animation: yadf 0.1s 1;
	overflow: hidden!important
	}
	@keyframes yadf {
	0% {
	transform: scale(1.1);
	stroke: #fff;
	filter: blur(5px)}
	}

	ytd-toggle-theme-compact-link-renderer:hover,
	tp-yt-paper-item.ytd-compact-link-renderer:hover {
	animation: yadg 0.2s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
	}
	@keyframes yadg {
	0% {
	letter-spacing: -0.5em;
	opacity: 0;
	}
	40% {
	opacity: 0.6;
	}
	100% {
	opacity: 1;
	}
	}
		
	ytd-feed-filter-chip-bar-renderer {
      background: linear-gradient(180deg, #00000067 10%, var(--yadtheme) 200%);
      box-shadow: 0 3px 0px 2px #00000036;
      background-blend-mode: exclusion, normal;
      backdrop-filter: blur(15px);
      border-radius: 0px 0px 10px 10px;
      transform-origin: top;
      z-index: 100;
      height: 65px;
      position: fixed;
    	z-index: 999;
      transition: transform 0.5s ease-in 0.3s, position 0.5s ease-in, opacity 0s ease 0.3s
   }

   ytd-feed-filter-chip-bar-renderer:not(:hover) {
      transform: translateY(-53px);
      opacity: 0;
   }
   ytd-feed-filter-chip-bar-renderer:not(:hover):not(:hover){
      transition: all 1s cubic-bezier(0, 0, 0, -0.3) 3s;
   }

	 
	ytd-feed-filter-chip-bar-renderer[component-style=FEED_FILTER_CHIP_BAR_STYLE_TYPE_HASHTAG_LANDING_PAGE][not-sticky] #chips-wrapper.ytd-feed-filter-chip-bar-renderer, ytd-feed-filter-chip-bar-renderer[component-style=FEED_FILTER_CHIP_BAR_STYLE_TYPE_CHANNEL_PAGE_GRID][not-sticky] #chips-wrapper.ytd-feed-filter-chip-bar-renderer,
	ytd-feed-filter-chip-bar-renderer[component-style=FEED_FILTER_CHIP_BAR_STYLE_TYPE_CHANNEL_PAGE_GRID] {
	display: contents
	}
	ytd-feed-filter-chip-bar-renderer[darker-dark-theme] #chips-wrapper.ytd-feed-filter-chip-bar-renderer,
	#chips-wrapper.ytd-feed-filter-chip-bar-renderer {
	transform: translateY(-50px);
	position: relative;
	border-top: 0px;
	border-bottom: 0px
  }
  /*backdrop*/
  tp-yt-iron-overlay-backdrop {
  display: none
  }







	/*live header*/
	[role="heading"][class="style-scope yt-live-chat-renderer"] {
	background: linear-gradient(-180deg, #ffebeb00 -200%, var(--yadtheme) 100%)!important;
	box-shadow: 0px 0px 9px 2px var(--ml)
	}

	ytd-destination-button-renderer:hover {
	background-color: var(--yadtheme)
	}
	ytd-destination-button-renderer,
	.offer-overview-item.selected.ytd-sponsorships-offer-renderer {
	background-color: #ffffff4a
	}
	tp-yt-paper-input.ytd-expandable-tab-renderer {
	--paper-input-container-input-color: #fff;
	--paper-input-container-underline_-_border-bottom: 1px solid #fff;
	}
	yt-live-chat-text-message-renderer[author-is-owner],
	yt-live-chat-banner-manager[has-active-banner],
	#menu.yt-live-chat-text-message-renderer {
	background: #0000!important
	}

	/*poll bg*/

	tp-yt-paper-item.ytd-backstage-poll-renderer[selected] .progress-bar.ytd-backstage-poll-renderer,
	#content-attachment.ytd-backstage-post-renderer ytd-video-renderer.ytd-backstage-post-renderer, #content-attachment.ytd-backstage-post-renderer ytd-playlist-renderer.ytd-backstage-post-renderer{
	background-color: var(--ml)
	}
	ytd-backstage-post-thread-renderer {
	border: 1px solid #ffffff14;
	}
	ytd-backstage-poll-renderer[show-poll-choice-border] tp-yt-paper-item.ytd-backstage-poll-renderer[selected] .choice-info.ytd-backstage-poll-renderer {
	border: 1px solid #51202000;
	}
	yt-icon.checked.ytd-backstage-poll-renderer {
	color: var(--yadtheme);
	}

	/*new*/

	.badge-style-type-simple.ytd-badge-supported-renderer {
	color: #fff;
	background: var(--yadtheme)
	}

	/*menu*/
	ytd-multi-page-menu-renderer[darker-dark-theme],
	tp-yt-paper-listbox,
	tp-yt-paper-dialog,
	yt-confirm-dialog-renderer[dialog][dialog][dialog],
	ytd-multi-page-menu-renderer,
	.ytp-popup,
	ytd-voice-search-dialog-renderer[dialog],
	ytd-sponsorships-offer-renderer[dialog][dialog][dialog],
	#card.ytd-miniplayer,
	.sbsb_a,
	.b3-modal-dialog .modal-dialog-content,
	ytd-two-column-browse-results-renderer[page-subtype=history] #secondary.ytd-two-column-browse-results-renderer,
	yt-tooltip-renderer::before,
	.ytp-ce-expanding-overlay-background, .iv-card,
	#dialog.ytcp-video-visibility-edit-popup,
	.html5-video-info-panel,
	ytd-pdg-buy-flow-renderer[darker-dark-theme][dialog][dialog][dialog] {
	background:url("http:dd.d/d.png") no-repeat , linear-gradient(180deg, #0000 10%, var(--yadtheme) 70%), url(/*[[yadbg]]*/)  var(--bg)!important;
	background-position: 80% 5.5%,center, center;
	background-size: 100%, cover, cover;
	background-blend-mode:screen, var(--screen);
	box-shadow: 0 0 0px 2px var(--yadtheme)!important;
	filter: drop-shadow( 0px 0px 9px var(--yadtheme));
	border-radius: 5px;
	text-shadow:  0px 1px 2px #000
	}
	.html5-video-info-panel {
	color: var(--yt-spec-text-primary)
	}

	#contents ytd-rich-grid-row,
	#contents ytd-rich-grid-row #contents {
	display: contents
	}
	.ytd-two-column-browse-results-renderer {
	--ytd-rich-grid-items-per-row: 4!important
	}	

	/*video info cards*/
	.iv-card:hover .iv-card-primary-link {
	color: var(--ml)!important
	}
	.iv-card, .iv-card a.iv-click-target, .iv-card a.iv-click-target:hover, .iv-card a.iv-click-target:focus {
	color: var(--st)
	}

	/*movie title*/

	#wide-clickable-area.ytd-movie-offer-module-renderer {
	background: var(--yadbg2)!important;
	box-shadow: 0 0 0px 1px var(--ml)!important;
	border-radius: 0px 0px 5px 5px
	}  
	#header.ytd-movie-offer-module-renderer {
	background: var(--yadbg2)!important;
	box-shadow: 0 0 0px 1px var(--ml)!important;
	border-radius: 5px 5px 0px 0px
	}

	/*yt-tooltip-renderer*/

	ytd-menu-popup-renderer,
	#tooltip.tp-yt-paper-tooltip,
	tp-yt-paper-toast,
	.ytp-ad-skip-button,
	ytcp-paper-tooltip-placeholder[type=explanatory] #tooltip.ytcp-paper-tooltip-placeholder {
	background-image: linear-gradient(200deg, var(--ml)  0%, var(--yadtheme) 100%);
	box-shadow: 0 0 0px 2px var(--yadtheme)!important;
	filter: drop-shadow( 0px 0px 9px var(--yadtheme));
	background-blend-mode: screen
	}


	/*search /voice box*/
	ytd-searchbox[desktop-searchbar-style=rounded_corner_dark_btn] #container.ytd-searchbox,
	ytd-searchbox[desktop-searchbar-style=rounded_corner_light_btn] #container.ytd-searchbox,
	#container.ytd-searchbox {
	border: 0px solid var(--yadtheme);
	background: #36384100;
	border-radius: 20px!important;
	border-right: 0px solid;
	box-shadow: 0 0 0 0;
	background-blend-mode: screen;
	transition: 0.5s all ease-in-out 
	}
	ytd-searchbox.ytd-masthead {
	border: 0px solid var(--yadtheme);
	/*background:  linear-gradient(90deg, #f000 10%, var(--yadtheme) 130%), var(--yadbg) var(--yadtheme);*/
	border-radius:20px!important;
	border-right: 0px solid;
	box-shadow: 0 0 0 2px /*[[yadtheme]]*/;
	background-blend-mode: screen!important;
  transition: 0.5s all ease-in-out ;
	opacity: 0.5

	}

	ytd-searchbox.ytd-masthead:focus-within  {
	background:  #fff0;
	backdrop-filter: blur(3px) contrast(1.3)!important;
	box-shadow: 0 0 0 2px var(--yadtheme);
	background-blend-mode: lighten!important;
	transition: all 1s!important
	}
	.sbfl_b {
	background: transparent;
	border: 0px;
	}

	ytd-searchbox[desktop-search-bar-big-tap-target] #search-form.ytd-searchbox {
	height: 35px;
	}
	ytd-searchbox[desktop-search-bar-big-tap-target] #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
	width: 25px;
	height: 25px;
	}

	#search-form.ytd-searchbox {
  border: 0px solid var(--yadtheme);
  background: transparent;
  border-radius: 20px;
	}
	#search-icon-legacy.ytd-searchbox, ytd-searchbox[desktop-search-bar-big-tap-target] #search-icon-legacy.ytd-searchbox {
	border: 0px solid var(--yadtheme);
	background: #fff0;
	border-radius: 0px 20px 20px 0px;
	border-left: 0px solid
	}
	#search-icon-legacy.ytd-searchbox:hover, #search-icon-legacy.ytd-searchbox:active {
	border: 0px solid var(--yadtheme);
	filter: drop-shadow( 0 0 0);
	background-color: #fff0;
	opacity: 0.7
	}
	ytd-searchbox[has-focus][desktop-searchbar-style=rounded_corner_dark_btn] #container.ytd-searchbox, ytd-searchbox[has-focus][desktop-searchbar-style=rounded_corner_light_btn] #container.ytd-searchbox {
	background: transparent
	}
	ytd-searchbox[has-focus] #container.ytd-searchbox {
	box-shadow: 0px 0px 0px 0px #fff0!important;
	border: 0px solid #0000
	}
	ytd-masthead[desktop-mic-background] #voice-search-button.ytd-masthead,
	#voice-search-button.ytd-masthead {
	background: #0000!important
	}

	ytd-searchbox[desktop-search-bar-big-tap-target] #search-icon-legacy.ytd-searchbox {
	border: 1px solid #0000;
	background-color: var(--yadtheme);
	height: 35px;
	border-radius: 20px;
	}

	ytd-guide-entry-renderer[active] {
	background-color: #ffffff0a;
	}  
	.sbpqs_a,
	.gsfs,
	.sbsb_i {
	color: var(--mt)
	}
	.sbfl_b {
	background: #0000;
	}
	.sbsb_d {
	background: var(--yadfc);	
	}
	.sbdd_b {
	background: #0000;
	border: 1px solid #0000;
	}

	#microphone.ytd-voice-search-dialog-renderer[state=try-again] #microphone-circle.ytd-voice-search-dialog-renderer {
	background: #ffffff14;
	box-shadow: 0 0 0px 1px var(--yadtheme);
	}
	/*top icons*/
	.yt-spec-icon-badge-shape,
	.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text,
	#voice-search-button.ytd-masthead .ytd-masthead[is-icon-button], #buttons.ytd-masthead .ytd-masthead[is-icon-button],
	#guide-icon.ytd-masthead,
	#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
	color: /*[[topicons]]*/!important
	}
	#guide-icon.ytd-app {
	fill: /*[[topicons]]*/
	}

	/*search input*/
	#container.ytd-searchbox input.ytd-searchbox {
	color: var(--mt)!important;
	}
	#container.ytd-searchbox {
	margin-left: 0px    
	}

	#search-input.ytd-searchbox-spt #search::-webkit-input-placeholder {
	color: var(--st)!important;
	}
	/*right column*/
	yt-icon.ytd-toggle-theme-compact-link-renderer {
	color: var(--yadtheme)!important;
	}

	/*playlist*/
	.header.ytd-playlist-panel-renderer {
	background: linear-gradient(-180deg, #ffebeb00 -60%, var(--yadtheme) 90%)!important;
	box-shadow: 0 0 9px 1px var(--yadtheme);
	border-radius: 10px 10px 0px 0px;
	}
	.playlist-items.ytd-playlist-panel-renderer {
	background: var(--yadbg) var(--bg)!important;
	background-blend-mode:  var(--screen);
	border-radius: 0px 0px 10px 10px;
	box-shadow: 0 0 9px 1px var(--yadtheme);
	}
	#container.ytd-playlist-panel-renderer {
	border: 1px solid #0000;
	}
	/*watch later*/
	ytd-browse[page-subtype=playlist], 
	#primary.ytd-two-column-browse-results-renderer,
	ytd-playlist-sidebar-renderer,
	ytd-browse[page-subtype=playlist] ytd-two-column-browse-results-renderer.ytd-browse {
	background-color: #0000;
	}
	ytd-alert-with-button-renderer[type=INFO], ytd-alert-with-button-renderer[type=SUCCESS] {
	background: #21212100	
	}
	#stats.ytd-playlist-sidebar-primary-info-renderer {
	color: #fff	
	}
	/*gaming*/
	ytd-carousel-header-renderer {
	background-color: var(--ml)66;
	}

	/*button*/
