style.css:
@-moz-document domain("ticketmaster.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.fFkPFa,
.keUAZm.link--has-other.link--open:after,
.sMkWQ.dropdown--no-border,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.ftFQcC:hover .image {
    transform: none;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.bCvzDL
{
	font-size: 1.5rem!important;
}

.eJPBuz,
.event-tile__sub-title,
.gLNIxp
{
	font-size: 1.2rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.box-ad,
.iSFqj,
.jTNWic,
.jUlVqD,
.leaderboard-ad,
.usabilla-live-button,

[data-testid="amex-smart-widget-container"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.jbztJF,
.bdLNKj,
.ldTmTh
{
	max-width: none!important;
}

.cuVFiS {
    width: 25%;
}
}

@-moz-document domain("ticketmaster.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
.jbztJF {
    flex: 0 0 99%;
}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("ticketmaster.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.defvPT:hover,
.kmGqMS:hover,
.links__list .haEqBP:active,
.links__list .haEqBP:active:hover,
.links__list .haEqBP:hover,
.links__list .haEqBP:hover,
.links__list .haEqBP:hover,
.links__list .haEqBP:visited:hover,
.nav--inverted .haEqBP:active,
.nav--inverted .haEqBP:active:hover,
.nav--inverted .sc-kHxTfl:hover,
.nav--inverted .sc-kHxTfl:hover,
.nav--inverted .sc-kHxTfl:visited:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.eDRbUo,
.eJPBuz,
.event-tile__sub-title,
.gLNIxp,
.gcqqIX,.nav--inverted .sc-kHxTfl:visited,
.jRyULD,
.kEXMEO,
.links__list .haEqBP,
.links__list .haEqBP:active,
.links__list .haEqBP:visited,
.nav--inverted .haEqBP:active,
.nav--inverted .sc-kHxTfl,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.bCvzDL,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.event-listing__date,
.event-listing__date .event-tile__sub-title,

time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

.sMkWQ,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/
selector
{
	color: #fff!important;
}


}

@-moz-document domain("ticketmaster.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.cGhGMV.nav--inverted,
.cmvjbI,
.dkKnzY,
.gGzMZo
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.DxnsP,
.NSza,
.cbmNZp:hover,
.defvPT:hover,
.drDtDD,
.fFkPFa,
.kdKbeU,
.links__list .haEqBP:hover,
.nav--inverted .sc-kHxTfl:hover
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.cBRhhk,
.jDStso,
.jLyRYo,
.jPQkWg
{
	background: linear-gradient(45deg, #012654 0%, #01748b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
	padding: 0 .5rem!important;
}

.cBRhhk:hover,
.jDStso:hover,
.jLyRYo:hover,
.jPQkWg:hover
{
	background: linear-gradient(135deg, #01748b 0%, #012654 100%)!important;
	color: #ff0!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.gGcqjb,
.iGaRLY
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.gGcqjb:hover,
.iGaRLY:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.brQHhw:not(.gradient--overlay-shadow),
.brQHhw.gradient--underlay.gradient--spotlight::after,
.brQHhw:not(.gradient--spotlight).gradient--underlay::after
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

}

@-moz-document domain("ticketmaster.com") {
/* Image Settings */

.accordion__item.event-listing__item,
.cuVFiS,
.fFpkeL,
.hero__adzerk--sidebar
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.accordion__item.event-listing__item:hover,
.cuVFiS:hover,
.fFpkeL:hover,
.hero__adzerk--sidebar:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

.eDRbUo
 {
	opacity: 1!important;
}

}

@-moz-document domain("ticketmaster.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


/* Arrows _______________________________________________________________________*/

/* Down _________________*/

.dropdown--no-border svg
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

.dropdown--no-border svg
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Search ___________________________________________________________________________*/

.search--container .sc-iukxot svg
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.search--container .sc-iukxot svg
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}


}

@-moz-document domain("ticketmaster.com") {
/* Scrollbar Settings */

/* Width 
__________________________________________________________________________________________________________________________________*/

 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
__________________________________________________________________________________________________________________________________*/


::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
