style.css:
@-moz-document domain("jeuxvideo.com") {
/* DarkJVC - Fix LethalVenom*/

.layout--withBackground .layout__breadcrumb, .layout--withBackground .layout__content, .layout--withBackground .layout__contentAfter, .layout--withBackground .layout__contentAside, .layout--withBackground .layout__contentBefore, .layout--withBackground .layout__contentMain, .layout--withBackground .layout__contentTop, .layout--withBackground .layout__contextBottom {
background-color: #1d1d1d;
}
.layout--withBackground {
background-color: #171717 !important;
background-image: none;
}

.topic-list li.dfp__atf~li:nth-of-type(2n) {
/* background: #fff; */
background: #2E2E2E;
}
.topic-list li.dfp__atf~li:nth-of-type(odd) {
/* background: #eef5fc; */
background: #2E2E2E;
}

    /*Logo Risitas au lieu de JV*/
    :root, :root.theme-light {
    --jv-logo: url(https://image.noelshack.com/fichiers/2018/29/6/1532128784-risitas33.png);
    }
    

/* Scrollbar Chrome*/
*::-webkit-scrollbar {
width: 10px ! important;
height: 10px ! important;
background: #1e1e1e ! important;
border: 1px solid #1e1e1e ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
min-height: 28px ! important;
background: #3b3b3b ! important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #1c1c1c ! important;
}



/* Card header */

.card-header{
    background: #3d3d3d !important;
}

.card-jv-forum > .card-header {
    background-color: #46556d;
    color: #cccccc;
    text-transform: uppercase;
}

/* Card body */ 

.card-body{
    color:#cccccc !important;
    background: #2d2d2d;
}

/* Titre info */

.titre-info-fofo{
    color:#cccccc !important;
}

/* Selection */
::selection {
background: #999;
color: #000;
}
::-moz-selection {
background: #999 ! important;
color: #000 ! important;
}

/*[[colorblind]]*/
/*[[couleur]]*/
/* URLS */
.txt-msg a,
.text-enrichi-default a {
color: #8f8f8f;
}

/* Placeholder */
::-webkit-input-placeholder {
color: #a3a3a3!important;
}
/* Version */
.fil-ariane-crumb:before {
content: "DarkJVC";
color: #888;
}
