style.css:
@-moz-document url-prefix("https://web.prisga.id/") {
body{
background-color: black;
color: white;
}

/* LOGIN */
.home{
background-image: url(https://img1.goodfon.com/original/1920x1080/4/b0/siniy-uzorchatyy-fon-temnaya.jpg);
background-color: black
}

.login100-more{
background-image: url(https://i.pinimg.com/originals/58/c3/33/58c33377dfcbb3022493dec49d098b02.jpg);
}

.login100-form{
background-color: #1b1b1b
}

.errors-container{
background-color: #990000;
color: white !important;
border-radius:10px;
padding: 0 15px 0 15px;
}

/* FIXED MENU */
.top-header{
position: fixed;
}

.sidebar-container{
position: fixed;
width:180px;
}

/* WHOLE CONTENT POSITION */
.content-page{
padding-top: 50px;
z-index: 1;
}

.sidebar-menu{
padding-left: 0;
}

/* TEXT COLOR */
div{
color: #F2F2F2;
}

a{
color: #F2F2F2 !important;
}

span{
color:#F2F2F2 !important;
}

p{
color: #D8D8D8 !important;
}


/* SIDEBAR MENU */
.main-menu .item-li a.custom-item-link i{
color: #F2F2F2;
}

.main-menu .item-li:hover a.custom-item-link i{
color: #F2F2F2;
}

.item-selected a.custom-item-link i{
color: #F2F2F2 !important;
}

.main-menu .item-selected{
background-color: #4A76A8;
border-radius: 5px;
}

.item-list .item-li{
border-radius: 5px !important
}

.item-list .item-li:hover{
background-color: #4A76A8;
border-radius: 5px !important
}

.main-menu .separator{
border-top: 1px solid #2d2d2d;
padding: 0;
margin: 5px 0 5px 0;
}

.footer-list{
border-top: none;
padding: 0;
margin-left: 10px;
}

.footer-list a.menu-nav{
color: #606770 !important;
}

.footer-list .copyright{
color: #606770 !important;
margin-top: 5px;
}

.item-li-main:hover .main-menu-action-button{
border-radius: 5px !important;
color: #F2F2F2 !important;
}

.main-menu .item-counter .counter{
background-color: green;
}

/* CARD */
.card{
background-color: #1B1B1B;
border: 1px solid #2d2d2d;
border-radius: 10px;
box-shadow: none;
margin-bottom: 10px;
padding:  0 10px 0 10px;
}

.post-item-time a{
color: #606770 !important;
}


/* card header */
.card-header:first-child{
padding: 10px 0 10px 10px;
}

.card-description{
display: none
}

.card-title{
color: white;
font-size: 20px;
font-weight: 600;
}

/* message page card */
.card-item.classic-item{
border-bottom: 1px solid #2b2b2b;
    padding-bottom: 0;
    margin-bottom: 15px;
}

.card-item .touch-item:hover{
background-color: #4A76A8;
border-radius: 10px;
}

.card-item .touch-item.active{
background-color: #4A76A8;
border-radius: 10px;
}

.card-item.classic-item a.card-body, .card-item.classic-item div.card-body, .card-item.message-item div.card-body{
background-color: transparent;
padding: 0;
margin-top: 0;
}

.message-item .card-header{
padding-bottom: 0;
padding-top: 5px;
}

.card-item.message-item:last-child{
padding-bottom: 10px;
border-bottom: 1px solid #2b2b2b
}

.cards-list{
padding: 10px;
background-color: #1b1b1b;
}

.card-status-text{
margin-top: 0;
}

.card-icon{
height: 45px;
width: 45px;
}

.card-date{
color: #606770 !important;
}

.card-action{
margin-top: 0;
}

/* card other content */
.repost-list{
border-radius: 10px;
background-color: #28405B;
margin: 10px 10px 0 10px !important;
border-left: 10px solid rgba(84, 117, 158, 0.35);
}

.posts-list-page-bordered .link-preview-item:first-child, .link-preview-item{
border-radius: 10px;
background-color: #B579AE;
margin: 10px 10px 0 10px;
color: white;
}

.link-preview-item span.link-title{
font-size: 14px;
}

.link-preview-item span.link-title, .link-preview-item span.link-description{
color: #f2f2f2 !important;
}

.custom-list-item{
padding: 10px 10px 10px 70px;
}

a.posted_link{
display: none;
}

.post-item .post-item-content{
color: #f2f2f2 !important;
}

/* COMMENT */
.media .comment-bubble{
background-color: #2b2b2b;
}

.input-group > .form-control:not(:first-child), .input-group > .custom-select:not(:first-child){
background-color: transparent;
}

.media .comment-bubble.left-top:after{
border-top-color: #2d2d2d;
}

.media .border{
background-color: #2b2b2b;
border: none !important;
}

a:not([href]):not([tabindex]){
background-color: #1b1b1b;
font-size: 16px
}

.item-footer-button i{
margin-top: 2px;
}

.custom-list-item .item-footer{
border-top: 1px solid #2b2b2b
}

.item-meta span, .item-meta a{
color: #a5a5a5 !important;
}

.post-text{
margin-bottom: 10px;
padding-left: 2px;
word-break:break-word;
}

/* MESSAGE CONTENT */
.message-item .card-status-text{
background: #4A76A8 !important;
border-radius: 10px;
padding: 5px 15px 5px 15px !important;
}

.card-item .green{
color: black !important;
border-radius: 10px;
padding: 1px 5px 1px 5px;
}

.message-item-right .card-icon{
top: 5px;
}

.comment-form input[type="text"]{
background-color: #2b2b2b;
border: none;
border-radius: 10px;
color: #f2f2f2
}

.message-item .post-img{
border-radius: 10px;
margin: 0 0 10px 0;
top: 5px
}


/* NEW POST TEXT AREA */
.profile_question_form{
background-color:  transparent;

}

.profile_question_form textarea{
background-color: transparent;
color: white;
}

.profile_question_form .form_actions{
background-color: transparent;
border-top: 1px solid #2d2d2d;
}

.profile_question_form .avatar{
left: 20px
}


/* BUTTON */
.btn, .primary_btn, button, .button {
    border-radius: 10px
}

.top-banner .prompt .button {
    background-color: #285473;
    color: white !important
}

.dropdown-menu {
    background-color: #212121;
    border: 2px solid #323232;
    border-radius: 10px
}
.flat_btn:hover {
    background-color: #ED7B09;
    border-radius: 10px;
    border: 1px solid #4A7652
}
.flat_btn {
    background-color: #A0570E;
    border-radius: 10px;
    border: 1px solid #A05234
}
.main-menu-action-button {
    background-color: #4A76A8;
}
button.blue:hover, input[type="button"].blue:hover, input[type="submit"].blue:hover, .button.blue:hover {
    border-radius: 10px !important
}
button.blue, input[type="button"].blue, input[type="submit"].blue, .button.blue {
    border-radius: 10px !important
}
.card-act{
background-color: #4A76A8;
}


/* Dropdown */
.dropdown-divider{
display: none
}

.dropdown-menu{
background-color: #1b1b1b
}

a.dropdown-item:hover{
background-color: #4A76A8;
}

/* PROFILE */
.profile-cover .cover-wrapper{
margin-top: 10px;
border-radius:15px;
background-color: black;
}

.page-profile .profile-cover-img{
border-radius:10px;
}

.profile-cover-img:active{
    background-size: contain;
	background-repeat: no-repeat;
    position:fixed;
    margin-top: 50px;
    transform: scale(1);
      -webkit-transition-duration: 0.3s;
    -moz-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
    transition-duration: 0.3s;
     z-index: 1;
}

.page-profile .fullname-line{
padding-left: 0;
}

.profile-info .profile-info-block{
padding:10px 0 15px 0;
}

/* PAGE TITLE OTHER */
.page-title-content{
background: transparent;
padding: 10px 10px 10px 20px;
}

.standard-page>*:first-child{
font-size: 20px;
color: white;
line-height: 20px;
}

.standard-page h3{
color: #f2f2f2;
}

.standard-page{
padding: 0 10px 15px 0;
}


.standard-page .errors-container{
filter: invert(1);
border-radius: 10px;
margin-bottom: 0;
color: #1b1b1b !important;
font-size: 16px;
margin-left: 0 !important;
}

.standard-page .warning-container{
margin-top: 0;
border-radius: 10px;
}

.user-profile .profile-content{
background: #1b1b1b !important;
border: 1px solid #2b2b2b;
border-radius: 10px;
}

.info-banner{
background: transparent;
}

.profile-content h1{
color: #f2f2f2 !important;
margin-top: 30px;
}

.profile-content .user-info{
padding: 20px 20px 0 40px;

}

.top-banner h1{
margin-top: 0;
margin-bottom: 10px
}

.preview-block .card-title{
margin-top: 5px;
font-size: 18px
}

.page-title-content-bottom-inner{
display: none;
}

.card-header .action-link{
margin-top:  40px;
}

.p-2{
margin-top: 20px
}

.gallery-item .item-inner, .community-item .item-inner, .market-item .item-inner{
    background-color: #2b2b2b;
    border-radius: 5px;
    border: 1px solid #2d2d2d;
}

/* SETTING PAGE */
.settings-page .profile-content{
background-color: #1b1b1b;
border-radius: 10px;
border: 1px solid #2b2b2b;
padding: 20px;
}

.settings-page h1.title{
padding-bottom: 15px;
border-bottom: 1px solid #2b2b2b;
}

.form-row{
margin-top: 0;
padding: 0 0 0px 10px;
border-bottom: none;
}

.profile-basics input{
background-color: #2b2b2b;
border-radius: 10px;
border: none;
color: #f2f2f2
}

.tab-pane{
margin-top: 0;
}

.cards-page .content-list{
border-top: 1px solid #2b2b2b;
padding: 10px 0 0 0;
}

.card-header{
background-color: transparent;
}

.card-item .blue{
background-color: transparent;
padding: 0;
}

.card-act.negative{
background-color: #4A76A8;
}

.ls-menu-item:hover, .ls-menu-subitem:hover, .ls-menu-item-selected, .ls-menu-item-selected:hover{
background-color: #4A76A8;
border-radius: 5px;
padding-left: 5px;
}

.ls-menu-item, .ls-menu-subitem{
margin-bottom: 5px;
margin-top: 5px;
padding-left: 5px;
}

/* REPORT */
.modal-content{
background-color: #1b1b1b;
border-radius: 10px;
border: 1px solid #2b2b2b;
}

.modal-header{
border-bottom: 1px solid #2b2b2b;
padding-left: 35px;
padding-right: 25px;
}

.modal-footer{
border-top: 1px solid #2b2b2b;
}

.box-menu-item{
font-size: 16px;
font-weight: 500;
}

.box-menu-item:hover{
border-radius: 10px;
background-color: #4A76A8;
}


.sn-sidenav{
	display: none;
}

@media (max-width: 992px){
    .sidebar-container{
    position: relative;
    }
    
    .sn-sidenav{
    display:block;
	background-color: #1b1b1b;
    }
    
    .has-bottom-footer #main-footer{
    background-color: black;                                   
    }
}


}

@-moz-document url("https://web.prisga.id/account/messages") {
.cards-list{
padding: 20px;
background-color: #1b1b1b;
}
}

@-moz-document url("https://web.prisga.id/") {
.top-header{
display:none
}
}

@-moz-document url("https://web.prisga.id/support") {
/* SUPPORT PAGE */
.remind-page .main-content{
background-color: #1b1b1b;
border-radius: 10px;
padding: 20px;
}

.standard-page p{
margin-bottom: 0;
}

.custom-form input[type="text"], .custom-form input[type="password"], .custom-form textarea{
background-color: #2b2b2b;
color: white !important;
border: none;
border-radius: 10px;
}
}

@-moz-document url("https://web.prisga.id/account/groups"), url("https://web.prisga.id/account/notifications") {
.gallery-item, .community-item, .market-item{
/*     margin-left:2%; */
    width: 104%;
    
}

.gallery-item .item-inner, .community-item .item-inner, .market-item .item-inner{
border-radius:10px;
}

.community-item-preview .info-badge, .market-item-preview .info-badge{
background-color: #1b1b1b;
opacity: 1;
}

.page-title-content{
background: #1b1b1b;
border: 1px solid #2b2b2b;
border-radius:10px;
padding: 10px 10px 15px 20px;
}

@media(max-width: 992px) {
    .card {
        position: relative;
        top: 0;
        left: 0;
        width: 100%
    }
}


}

@-moz-document url-prefix("https://web.prisga.id/search/hashtag") {
/* HASTAG PAGE */
.standard-page{
padding: 10px 10px 10px 10px;
}

.search-box{
border-top: 1px solid #2b2b2b;
padding-bottom: 15px !important;
}

.search-editbox-line input[type="text"]{
background-color: #2b2b2b;
border-radius: 10px;
border: none;
color: #f2f2f2
}

.mr-2, .mx-2{
margin: 0 !important
}

.top-banner h1{
text-align:center;
}
}

@-moz-document url("https://web.prisga.id/account/settings/blacklist") {
.card-header{
background-color: #1b1b1b;
}
}

@-moz-document url-prefix("https://web.prisga.id/account/chat/") {
.card-item.classic-item{
border-bottom: none;
    padding-bottom: 0;
    margin-bottom: 5px;
}
}
