}

signal>i {
    width: 20%;
    margin-left: 1px;
    display: inline-block;
    height: 40%;
    background-color: #fff;
	}

.rating-history {
    height: 200px !important;
}


@media (max-width: 799px){
.game-row__board {
    /*display: flex;*/
}
}

.game-row__board {
    flex: 0 0 12rem;
    margin-right: 1em;
}

.game-row {
    display: flex;
    align-items: stretch;
    height: 13rem;
    padding: .5em 1em;
    border-bottom: 1px solid #404040;
    position: relative;
    transition: background 150ms;
}

.game-row:nth-child(odd) {
     background: transparent;
}

.game-row__infos {
    flex: 1 1 100%;
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    margin: .5em 0;
    font-size: 0.7rem;
}

cg-board, .box, .upt__actions.btn-rack {
    box-shadow: none;
}


.analyse__controls .active {
    margin-top: 1px;
    padding-top: 3px;
}

.fbt.active {
 	background: transparent !important; 
    color: #fff;
}

.site-buttons .dropdown {
    box-shadow: none;
}

@media (max-width: 799px) and (orientation: portrait){
.main-board cg-board {
    border-radius: 0;
    width: 600px;
}
}
.chartjs-render-monitor {
    Display: flex !important;
    width: 100% !important;
    height: auto !important;
}

.puzzle-dashboard__metrics {
    display: flex;
    flex: 1 1 10ch;
    padding-top: 15px;
}

.puzzle-dashboard__global .puzzle-dashboard__metric strong {
    font-size: 1em !important;
}

.puzzle-dashboard__metric--fix i {
    font-size: 1em !important;
    opacity: .8;
    margin-right: -.1em;
    color: #fff;
}

.puzzle-dashboard__global .puzzle-dashboard__metric span {
    color: rgba(255,255,255,0.8);
    letter-spacing: 0px !important;
    font-size: 1rem;
}

.storm__start {
    display: none !important;
}

.storm__clock__time {
    font-size: 3em !important;
}

.storm--play {
    padding-top: 150px !important;
}

.storm__top::before {
    width: 3rem !important;
    height: 3rem !important;
}

.storm__solved__text {
    font-size: 3rem !important;
}

.user-show__social .user-actions {
    font-size: medium !important;
}

.round {
    padding-top: 110px !important;
}

rm6, .col1-rmoves-preload {
    margin-top: -110px !important;
}

