style.css:
@-moz-document domain("twitter.com") {
/* shapes */

.r-sdzlij,
.r-rs99b7,
.r-z2wwpe,
.r-1867qdf {
    border-radius: 0px
}


/* color variables */

:root {
    --mint-bright: #1abc9c;
    --mint-dark: #19987e;
    --mint-trans: #1abc9c0f;
    --mint-trans2: #1abc9c0b;
    --window: #292f34;
    --inputbg: #1f2327;
    --base: #212529;
    --lines: #51565b;
    --font2: #fff
}

/* font (doesnt work idk) */

:root {
    font-family: system-ui !important
}


/* colors */

body {
    background-color: var(--window) !important;
    scrollbar-color: #697076 #212529 !important
}

.r-yfoy6g, .r-1ila09b, .r-1uaug3w {
    background-color: var(--base)
}

.r-aaos50 {
    background-color: var(--inputbg)
}

.r-urgr8i {
    background-color: var(--mint-bright)
}

.r-13gxpu9, .r-1n1174f {
    color: var(--mint-bright)
}

.r-p1n3y5 {
    border-color: var(--mint-bright)
}

.r-1q3imqu {
    background-color: var(--mint-dark)
}

.r-zv2cs0 {
    background-color: var(--mint-trans)
}

.r-r72n3l {
    background-color: var(--mint-trans2)
}

.r-11z020y {
    background-color: rgba(0, 0, 0, 0.5)
}

.r-hjejmn {
    background-color: var(--mint-bright)
}
.r-1ila09b, .r-18bvks7, .r-o4zss7, .r-93ewon, .r-wrkyx5 {
    border-color: var(--lines)
}

.r-111h2gw {
    color: var(--font2);
    font-size: 11px
}


/* shadows */

.r-xnswec {
    box-shadow: rgba(0, 0, 0, .2) 0px 0px 15px, rgba(0, 0, 0, .15) 0px 0px 3px 1px;
}
    
}
