style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("spiegel.de"), domain("www.spiegel.de")
{

body {
background: transparent !important;
color: inherit !important;
}

* {
background: inherit !important;
/* color: inherit !important; */
}

/*====== Delete distracting elements ======*/
#spMainNavigation { display:none !important; }
#spNaviChannel { display:none !important; }
#spHeader { display:none !important; }
#spFooter { display:none !important; }
#spColumnRight { display:none !important; }
#spColumnAd { display:none !important; }
#spTopicHeader { display:none !important; }
#spSocialBookmark { display:none !important; }
#spLogin { display:none !important; }
#spHistory { display:none !important; }
#spArticleFunctions { display:none !important; }

.spInfoBox { display:none !important; }
.spMagazinkasten { display:none !important; }
.spSocialBookmarkBox { display:none !important; }
.spAsset { display:none !important; }
.spRessortTeaserBoxBottom { display:none !important; }
.spCBvideospecialsSpezial { display:none !important; }
.spChannelBlockHP { display:none !important;}
.spForumBoxBig { display:none !important; }
.spPartnerBar { display:none !important; }
.spMultimediaLeiste { display: none !important; }
.sonderkasten { display:none !important; }
.spSimpleContentBox { display:none !important; }
.spTagbox { display:none !important; }
.spTopicMultimedia { display:none !important; }
.spTop { display:none !important; }
.spArticleCredit { display:none !important; }

#spArticleColumn{
	float: left !important;
	width: none !important;
	overflow: hidden;
}

.spCenterpageContent .spTopThema,
.spCenterpageContent .spTeaser,
#home .spTopThema,
#home .spTeaser{
	border-top: 0 !important;
	border-bottom: 0;
}

}
