@-moz-document domain("www.keyhero.com") {
/* Main Colors and Changes*/
/* Color of the Page*/
body {
    background-color: #2c2c2c !important;
    color: white;
}
.description-block {
    background-color: #2C2C2C !important;
    color: white !important;
}
tr.head td {
    background-color: #2C2C2C !important;
    color: white !important;
}
div.new-post a {
    background-color: #2C2C2C !important;
    color: white !important;
}
/* Position and BG Color of Quote Box*/
.quote {
    position: static !important;
    margin-left: 225px !important;
    width: 700px !important;
    height: auto !important;
    letter-spacing: 0.5px !important;
    /* Change letter spacing */
    word-spacing: 10px !important;
    /* Change word spacing */
    line-height: 1.5pc !important;
    /* Change line height */
    font-size: 18px !important;
    /* Change font size here */
    text-align: left;
    /* Change text alingment here */
    text-decoration: none;
    /* Option for 'underline' for the current text*/
    font-family: "Roboto" !important;
    /* Change font family here */
    color: #fff;
}
/* Position and BG Color of User Input Box*/
.user-input-text {
    background-color: #0000 !important;
    position: static !important;
    margin-left: 225px !important;
    width: 700px !important;
    font-family: "Roboto" !important;
    /* Change font family here */
    font-size: 16px !important;
    /* Change font size here */
}
/* Position and BG Color of the WPM Chart and the Real-time WPM and Accuracy*/
.chart-ingame {
    /* WPM Graph Position */
    position: absolute !important;
    margin-top: -130px !important;
    margin-left: 175px !important;
}
.chart-quote {
    /* WPM Quote Graph Position */
    position: absolute !important;
    margin-top: 125px !important;
    margin-left: 175px !important;
}
.dl-horizontal dd {
    /* Real-time WPM and Accuracy */
    background-color: rgba(0, 0, 0, 0) !important;
}
.dl-horizontal {
    /*  Real-time WPM and Accuracy Position */
    position: static !important;
    margin-top: 50px !important;
    margin-left: -775px !important;
    color: #cdcdcda8 !important;
    /* Real-time WPM and Accuracy Font Color */
}

/* Highlight Color of the Current Word*/
/* Uncomment below to have highlight */

/* .quote-current {
    background: #008000 !important;
} */


/* No Highlight Color of the Current Word 
but with Underline*/
/* Comment below to have highlight */
.quote-current {
    background: #0000 !important;
    /* Keep the background transparent */
    text-decoration: underline;
    /* Underline of the Text */
    text-decoration-color: #ffffffab;
    /* Color of the Underline */
    text-decoration-style: solid;
    /* Style of the Underline */
    text-decoration-thickness: 0.25px;
    /* Thickness of the Underline */
    text-underline-position: under;
    /* Put Underline below Text 
     to don't have overlap in tails in text
     e.g. (g , y, p,) */
}

/* Color of the Typed Word */
/* .quote-left {
    background-color: #FFF;
} */

/* Rainbow Animated Color of the Typed Word */
.quote-left {
    -webkit-animation: linear rainbow 7.5s infinite;
}
/* Rainbow Animation */
@-webkit-keyframes rainbow {
    0% {
        color: red;
    }
    10% {
        color: orange;
    }
    20% {
        color: yellow;
    }
    30% {
        color: yellowgreen;
    }
    40% {
        color: #22ff3f;
    }
    50% {
        color: #00ffa2;
    }
    60% {
        color: cyan;
    }
    70% {
        color: #006eff;
    }
    80% {
        color: #7e00db;
    }
    90% {
        color: #d700d7;
    }
    100% {
        color: red;
    }
}

/* Highlight Color of the Wrong Word*/
.quote-error {
    background-color: rgba(204, 0, 0, .8) !important;
    text-decoration: none;
    /* Option for 'underline' for the error text*/;
    color: rgb(255, 255, 255) !important;
}
/* BG Color of the Quote Box when Error*/
.quote-bg-error {
    background-color: #710c0c59 !important;
}
/* Color of the Navigation Bar*/
.navbar-default .navbar-collapse,
.navbar-default .navbar-form {
    background: #2c2c2c !important;
}
.navbar-default {
    background-color: #2c2c2c !important;
    box-shadow: none !important;
}
/* Color of the Footer*/
footer {
    border-top: 1px solid #E5E5E5;
    color: #fff !important;
    padding: 15px 0;
    margin-top: 20px;
    background-color: #2c2c2c !important;
}
/* Color of the Language Selector*/
.form-control {
    color: #fff !important;
    font-weight: bold;
    background-color: #2c2c2c !important;
}
/* Correction and Error Colors */
.correction {
    margin: 15px 0;
    background-color: #00000000;
}
.quote-valid {
    color: #ffffff;
    background-color: #0000;
}
.quote-valid quote-acc-0 {
    background-color: #004c0673;
