/* ==UserStyle==
@name           Light Theme for YouTube Music
@author         Tristian Dedinas (https://github.com/Tech-How)
@namespace      Tech-How
@version        2.0
@description    A refreshing new look for YouTube Music
@supportURL     https://github.com/Tech-How/Light-Theme-for-YouTube-Music/issues
==/UserStyle== */

@-moz-document domain("music.youtube.com") {
    /* Sidebar */
    ytmusic-guide-renderer {
        animation: fluentLeft .3s forwards ease;
    }

    #contentContainer.tp-yt-app-drawer[opened] {
        animation: fluentRight .3s forwards ease;
    }
    
    /* Pop-ups and Dialogs */
    .style-scope.ytmusic-popup-container:not(ytmusic-notification-action-renderer):not(ytmusic-notification-text-renderer):not(ytmusic-video-download-toast-renderer) {
        animation: fluentUp .2s forwards ease;
    }
    
    ytmusic-dialog {
        animation: fadeIn .3s forwards ease;
    }
    
    /* Dropdowns and Items */
    tp-yt-iron-dropdown.tp-yt-paper-menu-button {
        animation: fluentDown .2s forwards ease;
    }
    
    ytmusic-search-box[opened] #suggestions {
        animation: fluentDownInline .2s forwards ease;
    }
    
    /* Player Page Enhancement */
    ytmusic-app-layout:not([is-mweb-modernization-enabled]):not([is-mobile-web]) > [slot="player-page"] {
        transform: none;
        animation: fluentSlideDownOut .65s forwards;
        animation-delay: .15s;
    }

    ytmusic-app-layout[player-page-open]:not([is-mobile-web]) > [slot="player-page"] {
        opacity: 0;
        transform: none;
        animation: fluentSlideUpIn .2s forwards;
        animation-delay: .1s;
    }
    
    /* Mini Player Enhancement */
    ytmusic-player-page[mini-player-enabled][player-page-open] #player.ytmusic-player-page {
        animation: miniPlayer .6s ease;
    }
}

@-moz-document domain("music.youtube.com") {
    /* Sidebar */
    tp-yt-paper-item.ytmusic-guide-entry-renderer, tp-yt-paper-item.ytmusic-guide-entry-renderer:hover {
        transition: background .1s;
    }
    
    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal, html.inactive-player.no-focus-outline body ytmusic-app ytmusic-app-layout#layout.style-scope.ytmusic-app tp-yt-app-drawer#guide.style-scope.ytmusic-app div#contentContainer.style-scope.tp-yt-app-drawer div#guide-wrapper.style-scope.ytmusic-app div#guide-content.style-scope.ytmusic-app ytmusic-guide-renderer#guide-renderer.style-scope.ytmusic-app div#sections.style-scope.ytmusic-guide-renderer ytmusic-guide-section-renderer.style-scope.ytmusic-guide-renderer div#buttons.style-scope.ytmusic-guide-section-renderer yt-button-renderer.style-scope.ytmusic-guide-section-renderer yt-button-shape button.yt-spec-button-shape-next.yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--icon-leading:hover, html.no-focus-outline body ytmusic-app ytmusic-app-layout#layout.style-scope.ytmusic-app tp-yt-app-drawer#guide.style-scope.ytmusic-app div#contentContainer.style-scope.tp-yt-app-drawer div#guide-wrapper.style-scope.ytmusic-app div#guide-content.style-scope.ytmusic-app ytmusic-guide-renderer#guide-renderer.style-scope.ytmusic-app div#sections.style-scope.ytmusic-guide-renderer ytmusic-guide-section-renderer.style-scope.ytmusic-guide-renderer div#buttons.style-scope.ytmusic-guide-section-renderer yt-button-renderer.style-scope.ytmusic-guide-section-renderer yt-button-shape button.yt-spec-button-shape-next.yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--icon-leading:hover {
