    }
    
    /* List Item Artwork Hover Animation */
    .thumbnail-overlay.ytmusic-responsive-list-item-renderer {
        transition: opacity .1s;
    }
    
    /* Top Result Artwork Hover Animation */
    .thumbnail-overlay.ytmusic-card-shelf-renderer {
