    ytmusic-chip-cloud-chip-renderer[enable-bauhaus-style][chip-style="STYLE_LARGE_TRANSLUCENT_AND_SELECTED_WHITE"][is-selected]:not(.iron-selected) a.ytmusic-chip-cloud-chip-renderer {
