        /* Context Menus */
    .style-scope.ytmusic-multi-select-menu-renderer {
        transition: background .1s;
    }

    .style-scope.ytmusic-menu-popup-renderer, #items.ytmusic-menu-popup-renderer > .ytmusic-menu-popup-renderer:hover {
        transition: background .1s;
    }

    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text, ytmusic-multi-select-menu-item-renderer:hover {
        transition: background .1s;
    }

    /* Account Menu */
    #endpoint.yt-simple-endpoint.ytd-compact-link-renderer, #endpoint.yt-simple-endpoint.ytd-compact-link-renderer:hover {
