        /* Create Playlist */
    ytmusic-dialog tp-yt-paper-dialog-scrollable.style-scope.ytmusic-dialog.no-padding.scrolled-to-bottom div#scrollable.scrollable.style-scope.tp-yt-paper-dialog-scrollable div#content.style-scope.ytmusic-dialog ytmusic-playlist-form.style-scope.ytmusic-dialog iron-pages.style-scope.ytmusic-playlist-form div#general-pane.style-scope.ytmusic-playlist-form.iron-selected div.actions.style-scope.ytmusic-playlist-form yt-button-renderer.style-scope.ytmusic-playlist-form yt-button-shape button.yt-spec-button-shape-next.yt-spec-button-shape-next--filled.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--size-m {
