        /* Editable Playlist Page */
    yt-button-renderer.style-scope > yt-button-shape:nth-child(1) > button:nth-child(1):hover, ytmusic-menu-renderer.style-scope:nth-child(5) > yt-button-shape:nth-child(3) > button:nth-child(1):hover {
        transition: background .1s;
    }

    div#content.style-scope.ytmusic-dialog ytmusic-multi-select-menu-bar.style-scope.ytmusic-dialog div#multiSelectMenu.style-scope.ytmusic-multi-select-menu-bar ytmusic-menu-renderer.style-scope.ytmusic-multi-select-menu-bar yt-button-shape#button-shape.style-scope.ytmusic-menu-renderer button.yt-spec-button-shape-next.yt-spec-button-shape-next--text.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--icon-button:hover {
        transition: background .1s;
    }

    ytmusic-responsive-list-item-renderer[is-checked] {
        transition: background .1s;
