    ytmusic-responsive-list-item-renderer, ytmusic-responsive-list-item-renderer[play-button-state="loading"], ytmusic-responsive-list-item-renderer[play-button-state="playing"], ytmusic-responsive-list-item-renderer[play-button-state="paused"] {
