    ytmusic-responsive-list-item-renderer[is-bauhaus-web-playlist-detail-page-redesign-enabled][is-checked], ytmusic-responsive-list-item-renderer[is-bauhaus-web-album-detail-page-redesign-enabled][is-checked] {
