        /* Edit Playlist Metadata */
    #general-pane > div.actions.style-scope.ytmusic-playlist-form > yt-button-shape:nth-child(3) > button:hover {
        transition: background .1s;
    }
}

@-moz-document url-prefix("https://music.youtube.com/watch") {
    /* Player Page */
    ytmusic-app-layout[is-bauhaus-sidenav-enabled].content-scrolled #mini-guide-background.ytmusic-app-layout {
        transition: opacity .2s;
    }

    #nav-bar-background.ytmusic-app-layout {
        transition: background .2s;
    }
}

@-moz-document url-prefix("https://music.youtube.com/playlist?list=OLAK") {
    /* Album Page */
    .left-items.ytmusic-responsive-list-item-renderer {
        transition: box-shadow .2s;
    }
}

@-moz-document domain("music.youtube.com") {
    :root {
        --ytm-shadow-big: 3px 3px 15px rgba(0, 0, 0, .5);
        --ytm-shadow-medium: 0px 0px 4px 2px rgba(0,0,0,0.3);
        --ytm-shadow-small: 0px 0px 3px 2px rgba(0,0,0,0.3);
        --ytm-shadow-mini: 0px 0px 3px 1px rgba(0,0,0,0.3);
        --ytm-shadow-browse: 1px 2px 8px 3px rgba(0,0,0,0.27);
        --ytm-shadow-search: 0px 6px 6px 1px rgba(0, 0, 0, 0.26);
        --ytm-shadow-dropdown: 0px 0px 10px -4px rgba(0,0,0,0.60);
        --ytm-shadow-dialog: 1px 1px 8px 0px rgba(0,0,0,0.3);
        --ytm-shadow-toast: 7px 7px 10px rgba(0, 0, 0, .5);
    }
}

@-moz-document domain("music.youtube.com") {
    :root {
        --ytm-radius-big: 10px;
        --ytm-radius-small: 7px;
        --ytm-radius-mini: 3px;
    }
}

@-moz-document domain("music.youtube.com") {
    #tabsContent.tp-yt-paper-tabs, yt-button-renderer yt-formatted-string.yt-button-renderer, .tab-content.tp-yt-paper-tab, tp-yt-paper-button.ytmusic-playlist-form, .tab.ytmusic-item-section-tab-renderer, .tab.selected.ytmusic-item-section-tab-renderer, .more-button.ytmusic-shelf-renderer tp-yt-paper-button.ytmusic-shelf-renderer, tp-yt-paper-button.ytmusic-toggle-button-renderer yt-icon.ytmusic-toggle-button-renderer~yt-formatted-string.ytmusic-toggle-button-renderer, .style-scope.ytmusic-subscribe-button-renderer, .sign-in-link.ytmusic-nav-bar, .done-button.ytmusic-uploads-complete, .library-button.ytmusic-uploads-complete, .stop-button.ytmusic-upload-progression {
        text-transform: none;
    }

    .tab.ytmusic-tabs {
        text-transform: capitalize;
    }

    .style-scope.yt-button-renderer.style-text {
        text-transform: none;
    }
}
@-moz-document domain("music.youtube.com") {
/* Global Site */

/* Variables */
:root {
    --ytm-bg-app: #f4f4f4;
    --ytm-bg-content: #fff;
    --ytm-content: #262626;
    --ytm-hover: #eee;
    --ytm-hover-app: #e5e5e5;
    --ytm-hover-invert: #262626;
    --ytm-selected: #ddd;
    --ytm-seperator: #ccc;
    --ytm-seperator-invert: #383838;
    --ytm-text: #000;
    --ytm-text-invert: #fff;
    --ytm-placeholder: #404040;
    --ytm-btn-mono: #000;
    --ytm-btn-mono-invert: #fff;
    --ytm-btn-tonal: var(--ytm-selected);
    --ytm-accent: /*[[userYTMAccent]]*/;
    --ytm-accent-invert: /*[[userYTMAccentInvert]]*/; /* Used when the background is black instead of white. */
    --ytm-toggle: #3387ce;
    --ytm-toggle-bg: #3ea6ff;
    --ytm-progress-primary: var(--ytm-accent);
    --ytm-progress-secondary: #aaa;
    --ytm-progress-tertiary: #ccc;
    --ytm-scrollbar: #aaa;
}

/* Animations */
@keyframes fluentDown {
    0% {
        opacity: 0;
        transform: translateY(0px);
    }
    100% {
        opacity: 1;
        transform: translateY(6px);
    }
}

@keyframes fluentUp {
    0% {
        opacity: 0;
        transform: translateY(6px);
    }
    100% {
        opacity: 1;
        transform: translateY(0px);
    }
}

@keyframes fluentLeft {
    0% {
        opacity: 0;
        transform: translateX(8px);
    }
    100% {
        opacity: 1;
        transform: translateX(0px);
    }
}

@keyframes fluentRight {
    0% {
        opacity: 0;
        transform: translateX(-12px);
    }
    100% {
        opacity: 1;
        transform: translateX(0px);
    }
}

@keyframes fluentDownInline {
    0% {
        opacity: 0;
        transform: translateY(-6px);
    }
    100% {
        opacity: 1;
        transform: translateY(0px);
    }
}

@keyframes fluentSlideUpIn {
    0% {
        opacity: 0;
        transform: translateY(50%);
    }
    100% {
        opacity: 1;
        transform: translateY(0px);
    }
}

/* The below keyframe must return to its original position after the element animates out, otherwise the miniplayer would remain hidden beneath the viewport. */
@keyframes fluentSlideDownOut {
    0% {
        opacity: 1;
        transform: translateY(0);
    }
    25% {
        opacity: 0;
        transform: translateY(50%);
    }
    50% {
        opacity: 0;
        transform: translateY(50%);
    }
    75% {
        opacity: 0;
        transform: translateY(calc(100% + var(--ytmusic-player-bar-height) * 2 - 15px));
    }
    100% {
        opacity: 1;
        transform: translateY(calc(100% + var(--ytmusic-player-bar-height) * 2 - 15px));
    }
}

@keyframes miniPlayer {
    0% {
        opacity: 0;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

@keyframes fadeIn {
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

@keyframes shadowIn {
    0% {
        box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0);
    }
    100% {
        box-shadow: var(--ytm-shadow-search);
    }
}

/* Main Style */
/* Header/Navigation Bar */
ytmusic-nav-bar .left-content [aria-label=Home] picture, ytmusic-app[is-bauhaus-sidenav-enabled] ytmusic-guide-renderer [aria-label=Home] picture {
  display: none !important;
}

ytmusic-nav-bar .left-content [aria-label=Home], ytmusic-app[is-bauhaus-sidenav-enabled] ytmusic-guide-renderer [aria-label=Home] {
    background: url("data:image/svg+xml;base64,");
    margin-top: -3px;
    border-radius: 5px;
    width: 120px;
    height: 30px;
    background-repeat: no-repeat;
    margin-left: -5px;
}

ytmusic-app[is-bauhaus-sidenav-enabled][is-hidden-guide-page] ytmusic-guide-renderer [aria-label=Home] {
    margin-top: 5px !important;
}

ytmusic-app[is-bauhaus-sidenav-enabled] ytmusic-guide-renderer [aria-label=Home] {
    margin-top: 3px !important;
}

#nav-bar-background.ytmusic-app-layout {
    background: var(--ytm-bg-content);
}

/* Navigation Progress */
yt-page-navigation-progress.ytmusic-app {
    background: transparent;
}

#progress.yt-page-navigation-progress {
    background: var(--ytm-accent);
}

ytmusic-app-layout[is-bauhaus-sidenav-enabled] #nav-bar-divider.ytmusic-app-layout {
    border-top: none;
}

ytmusic-app-layout[is-bauhaus-sidenav-enabled] #nav-bar-background.ytmusic-app-layout {
    border-bottom-color: var(--ytm-seperator) !important;
}

/* Welcome Image */
.background-gradient.ytmusic-immersive-carousel-shelf-renderer {
    display: none;
}

/* Sidebar */
ytmusic-app[is-bauhaus-sidenav-enabled] #guide-wrapper.ytmusic-app, ytmusic-app-layout[is-bauhaus-sidenav-enabled].content-scrolled #mini-guide-background.ytmusic-app-layout {
    background: var(--ytm-bg-content);
}

ytmusic-nav-bar[is-bauhaus-sidenav-enabled] .left-content.ytmusic-nav-bar yt-icon-button.ytmusic-nav-bar > button:nth-child(1) {
    background: transparent !important;
}

#divider.ytmusic-guide-section-renderer, ytmusic-guide-signin-promo-renderer[is-collapsed] {
    border-top-color: var(--ytm-seperator);
}

ytmusic-guide-entry-renderer[active] tp-yt-paper-item.ytmusic-guide-entry-renderer {
    background: var(--ytm-selected);
}

tp-yt-paper-item.ytmusic-guide-entry-renderer:hover {
