@-moz-document domain("music.youtube.com") {
/* Global Site */

/* Variables */
:root {
    --ytm-bg-app: #f4f4f4;
    --ytm-bg-content: #fff;
    --ytm-content: #262626;
    --ytm-hover: #eee;
    --ytm-hover-app: #e5e5e5;
    --ytm-hover-invert: #262626;
    --ytm-selected: #ddd;
    --ytm-seperator: #ccc;
    --ytm-seperator-invert: #383838;
    --ytm-text: #000;
    --ytm-text-invert: #fff;
