==/UserStyle== */
/*
Версия 10.07.2021 c фиксами для Firefox и TorBrowser
*/
@-moz-document url-prefix("https://foxhound"),
domain("foxhound.cc"),
domain("www.foxhound.cc"),
domain("6k5boa4nveidv7cdpjminvak6y4dklvhbpsiuhndnzuxbfyraxtlftad.onion") {
	/* скрытие досок: вписываете доски которые хотите скрыть,
   или наоборот удалите строку чтобы показать */
	a[href$="/trains"],
	a[href$="/n"],
	a[href$="/lovesis"],
	a[href$="/mc"],
	a[href$="/ma"],
	a[href$="/u"],
	a[href$="/1chan"],
	a[href$="/a"],
	a[href$="/vn"] {
		display: none !important;
	}

	/*  измененные цвета: если используете Stylus,
	можете просто визуально поменять значения цветов
	этого блока чтобы изменить стиль всего сайта,
	или ввести коды цветов вручную */
	* {
		--color-posts: #2E343E;
		--color-back: #242630;
		--color-back-dark: #0a0a0d;
		--color-like-sidebar: #2c333d;

		--color-text: #BCBCBC;
		--color-text-green: #57be57;
		--color-text-ban: #cd5ca0;
		--color-text-white: #DEDEDE;
		--color-hover: #3F424F;

		--color-green: #fd8b00e8;
		--color-green-dark: #8b3417d6;
		--color-green-dark-dark: #4e2c16;
		--color-cyan: #81F8E0;
		--color-code: #08b327;
	}

	/* Вкорачивание svg логотипа заместо png */
	/* скрываем старый */
	#sidebar > div.sidebar-logo > a > div > img,
	.sidebar-logo > a > img {
		display: none !important;
	}
	/* только для ПК и планшетов */
	@media (min-width: 768px) {
		/* чтобы svg не масштабировалось во всю ширину*/
		.sidebar-logo {
			max-inline-size: 64px;
			margin-left: 35%;
			max-height: 80px;
		}
		/* подсовываем svg, указываем несколько источников,
		если один отлетит, браузер берет последний указанный */
		.sidebar-logo > a:before {
			content: url("https://dl.uploadgram.me/60e24d51e2d55g?raw");
			content: url("https://dl.uploadgram.me/60e24d5ab9f3eh?raw");
			content: url("https://files.catbox.moe/2qni9p.svg");
		}
	}
	/* тоже самое, только для маленьких экранов */
	@media (max-width: 767px) {
		#sidebar > div.sidebar-logo > a:before {
			content: url("https://dl.uploadgram.me/60e354a5e4a45h?raw");
			content: url("https://dl.uploadgram.me/60e35e6dcfe86g?raw");
			content: url("https://files.catbox.moe/0pd6b1.svg");
		}
		/* подгонка размера */
		.sidebar-logo {
			max-inline-size: 130px;
			margin-left: 35%;
			padding-top: 2%
		}
	}

	/* svg-логотип Ютюпа*/
	img.post-embed-play-btn {
		display: none!important;
	}
	.post-embed-overlay,
	.post-embed-overlay:after {
		content: url("https://upload.wikimedia.org/wikipedia/commons/0/09/YouTube_full-color_icon_%282017%29.svg");
		zoom: .4;
		padding-left: 80%!important;
		padding-top: 60%!important;
		filter: drop-shadow(0px 0px 10px #000);
	}
	/* скрыть текстовую ссылку на ютуб, если ролик уже прикреплен к посту */
	a[href*="https://youtu.be/"],
	a[href*="youtu.be"],
	a[href*="https://www.youtube.com/"],
	a[href*="youtube.com"] {
		display: none;
		content: "";
	}
	/* оформление ссылки на внешние webm/mp4 */
	a[href*=".webm"],
	a[href*=".mp4"],
	a[href*="dl.uploadgram.me"] {
		color: var(--color-posts)!important;
		font-size: 0.001em !important;
		white-space: nowrap;
		overflow: hidden;
	}
	a[href*=".webm"]::before {
		content: url("https://dl.uploadgram.me/60e89b2b4439ah?raw");
		zoom: 1.75;
		transition: 1s cubic-bezier(0, 1, 0, 1)!important;
		-moz-transition: 0.1s cubic-bezier(0, 1, 0, 1)!important;
	}
	a[href*=".webm"]:hover:before {
		filter: drop-shadow(0px 0px 4px var(--color-back-dark));
	}
	a[href*=".mp4"]::before {
		content: url("https://dl.uploadgram.me/60e89b2aabb03h?raw");
		zoom: 1.75;
		transition: 1s cubic-bezier(0, 1, 0, 1)!important;
		-moz-transition: 0.1s cubic-bezier(0, 1, 0, 1)!important;
	}
	a[href*=".mp4"]:hover:before {
		filter: drop-shadow(0px 0px 4px var(--color-back-dark));
	}
	a[href*="dl.uploadgram.me"]::before {
		content: url("https://dl.uploadgram.me/60e8d4d93690eh?raw");
		zoom: 1;
		line-height: 24px;
		filter: none;
		transition: 1s cubic-bezier(0, 1, 0, 1)!important;
		-moz-transition: 0.1s cubic-bezier(0, 1, 0, 1)!important;
	}
	a[href*="dl.uploadgram.me"]:hover:before {
		filter: drop-shadow(0px 0px 10px var(--color-green));
		padding-left: 3px;
		transition: 1s cubic-bezier(0, 1, 0, 1)!important;
		-moz-transition: 0.1s cubic-bezier(0, 1, 0, 1)!important;
	}

	/* привязка всего зеленого к одному цвету */
	a {
		color: var(--color-green) !important;
	}

	a:hover {
		color: var(--color-green-dark) !important;
	}

	.post-button i {
		color: var(--color-green) !important;
	}

	.post-button i:hover {
		color: var(--color-green-dark) !important;
	}

	.btn,
	.nav-item a {
		background: var(--color-green-dark) !important;
		color: var(--color-text-white) !important;
		border: unset !important;
		border-radius: unset !important;
	}

	.nav-item {
		margin-left: unset !important;
	}

	.btn:hover,
	.nav-item:hover a {
		background: var(--color-green-dark-dark) !important;
		color: var(--color-text) !important;
	}

	.nav-item.active a {
		background: var(--color-green-dark-dark) !important;
		color: var(--color-text) !important;
		border: 1px solid var(--color-green) !important;
		margin-top: -1px;
		/* граница */
	}
	address,
	.im-address {
		color: var(--color-green) !important;
		border-color: var(--color-green-dark);
	}

	::selection {
		background: var(--color-back-dark);
		color: var(--color-green);
	}

	*:focus {
		outline-color: var(--color-green-dark-dark) !important;
	}
	.form-control:focus {
		border-color: var(--color-green-dark-dark) !important;
		box-shadow: inset 0 1px 1px rgb(0 0 0 / 8%), 0 0 8px var(--color-green-dark) !important;
	}

	html,
	body {
		-webkit-tap-highlight-color: var(--color-green-dark-dark) !important;
		-tap-highlight-color: var(--color-green-dark-dark) !important;
	}
	/* полоска загрузки */
	.__cov-progress {
		background: linear-gradient(to right, var(--color-green), var(--color-green-dark), var(--color-green-dark-dark));
	}

	/*Скрываемая панель с тредами*/
	#content > div > div.threads-scroll-spy.hidden-md.hidden-sm.hidden-xs > ul {
		background: var(--color-back-dark)!important;
	}

	#content > div > div.threads-scroll-spy.hidden-md.hidden-sm.hidden-xs > ul > li > a:hover {
		color: var(--color-text-white)!important;
	}
	.threads-scroll-spy ul li:hover,
	.threads-scroll-spy ul li.active {
		border-color: var(--color-green);
	}

	/* убираем скроллбар у списка досок */
	.sidebar::-webkit-scrollbar,
	#sidebar::-webkit-scrollbar,
	.sidemenu-boards::-webkit-scrollbar,
	.sidemenu-boards-list-box::-webkit-scrollbar,
	.sidemenu-boards-list::-webkit-scrollbar {
		width: 0px!important;
		background: transparent!important;
	}

	/* фон */
	body {
		background: var(--color-back) !important;
	}

	div#content {
		background: transparent !important;
	}

	.sidebar {
		background: var(--color-like-sidebar);
		box-shadow: -27px 0px 18px -19px #0006 inset;
	}

	span.sidemenu-board-title {
		display: none;
	}
	span.sidemenu-board-dir:before {
		line-height: 1em;
		color: var(--color-text-white);
		font-size: 13px;
	}

	/* оформление поста, формы с новым постом */
	.block.post,
	.reply-form {
		background-color: var(--color-posts) !important;
		border: unset !important;
		box-shadow: 0 1px 4px #111111 !important;
	}

	.reply-form-message {
		color: var(--color-text);
		background-color: var(--color-back) !important;
		border: 1px solid var(--color-green-dark-dark) !important;
		box-shadow: 0 !important;
	}

	/* скролл для длинных постов */
	.block.post::-webkit-scrollbar,

	.post .post-body .post-body-message::-webkit-scrollbar-track-piece,
	.post .post-body .post-body-message::-webkit-scrollbar-track,
	.post .post-body .post-body-message::-webkit-scrollbar-button,
	.post-body::-webkit-scrollbar-track-piece,
	.post-body::-webkit-scrollbar-track,
	.post-body::-webkit-scrollbar-button {
		border-radius: 10px;
		background: var(--color-posts) !important;
	}
	.post .post-body .post-body-message::-webkit-scrollbar-thumb,
	.post-body::-webkit-scrollbar-thumb {
		border-radius: 10px;
		background: var(--color-text) !important;
	}

	/* ограничение ширины поста в зависимсоти от размера шрифта*/
	.post-body-message,
	.post,
	.post .post-body .post-body-message {
		/* max-height: calc(40em * 1.6);
		height: 100% !important; */
		max-width: calc(30em * 1.65);
	}

	/* Выпадающий список Личности */
	select {
		border-color: var(--color-green-dark);
		appearance: none;
		background: var(--color-posts);
		padding: 1px 5px 0px 5px;
		color: var(--color-text-white);
	}
	/* информация о вложении */
	.post-img figcaption {
		font-size: 80%;
		background: #000;
		padding: 0.2em 0.4em;
	}
	/* удалить и скрыть пикчу*/
	.post-img .post-img-buttons {
		position: absolute;
		top: 0;
		right: 0;
		margin-top: -18px;
		line-height: 18px;
		transition: margin-top 0.2s, opacity 0.2s;
		background: #000;
		z-index: 1;
	}

	/* модальное окно */
	.modals-content {
		color: var(--color-text);
		background-color: var(--color-back-dark) !important;
		border: 1px solid var(--color-green-dark-dark) !important;
		box-shadow: unset !important;
	}

	/* выпадающее меню */
	.dropdown-menu {
		background-color: var(--color-back-dark) !important;
		border: none;
		box-shadow: 0 6px 12px var(--color-green-dark-dark);
	}
	.dropdown-header {
		color: var(--color-text-white) !important;
	}
	div.headmenu-buttons.headmenu-buttons-right > div > div > ul > li > a:hover {
		background-color: var(--color-back) !important;
		color: var(--color-text) !important;
	}

	/* текстовое поле */
	textarea {
		color: var(--color-text);
		background-color: var(--color-back);
	}

	/* разделитель тредов */
	div.separator {
		border-color: var(--color-hover);
	}

	/* ОП-пост*/
	div.block.post-op {
		border: 1px solid var(--color-green-dark-dark) !important;
	}

	/* текст постов (включая ОП) */
	div.post-body {
		color: var(--color-text) !important;
	}

	/* название треда в ОП-посте */
	span.post-id {
		color: var(--color-text);
	}

	.post .post-header .post-date,
	.panel-heading,
	.fa:before {
		color: var(--color-text-white) !important;
	}

	/* футер поста (кнопки скрыть, ответить) */
	div.post-footer {
		border: unset !important;
	}

	/* плашка с названием доски вверху страницы */
	div.headmenu {
		background-color: var(--color-back);
		border-bottom: none;
	}

	div.headmenu-title > span,
	div.headmenu-title > span * {
		color: var(--color-text) !important;
	}

	/* кнопочки */
	button.btn.btn-xs {
		color: var(--color-text);
		background-color: var(--color-green-dark);
		border-color: var(--color-green-dark);
	}

	.btn,
	.nav-item a,
	.pagination li {
		text-shadow: 1px 1px 1px black;
	}

	button {
		filter: none;
		transition: 1s cubic-bezier(0, 1, 0, 1)!important;
		-moz-transition: 0.5s cubic-bezier(0, 1, 0, 1)!important;
	}
	button:active {
		filter: drop-shadow(0px 0px 4px var(--color-back-dark));
	}

	/* спойлер */
	mark,
	.spoiler {
		color: var(--color-text);
		background: none;
		text-shadow: var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 3px, var(--color-text) 0 0 5px, var(--color-text) 0 0 10px, var(--color-text) 0 0 10px;
		transition: 1s cubic-bezier(0, 1, 0, 1);
		opacity: 50%;
		filter: blur(3px) saturate(0%);
	}

	mark:hover,
	.spoiler:hover {
		color: var(--color-text);
		background: none;
		text-shadow: transparent 0 0 0px;
		opacity: 100%;
		transition: 1s cubic-bezier(0, 0.75, 0, 1);
		filter: none;
	}

	mark a,
	.spoiler a {
		color: var(--color-text) !important;
		transition: 1s cubic-bezier(0, 0.75, 0, 1) !important;
	}

	mark:hover a,
	.spoiler:hover a {
		color: var(--color-green) !important;
		background: none;
		text-shadow: transparent 0 0 0px;
		opacity: 100%;
		filter: none;
		transition: 1s cubic-bezier(0, 0.75, 0, 1) !important;
	}

	/* блок цитирования */
	pre {
		background-color: var(--color-text);
		color: var(--color-back);
	}

	/* код */
	code {
		background-color: #222222;
		color: var(--color-code);
		font: 0.9em "DejaVu Sans Mono", monospace !important;
		margin-top: 2em;
		padding: 0.5em;
		animation: blink 0.7s step-end infinite alternate;
		overflow: hidden;
		border-right: solid 2px;
		border-radius: 0!important;
		display: block;
	}
	@keyframes blink {
		50% {
			border: 0;
		}
	}

	/* NSFW */
	.post-img .post-img-nsfw-label,
	.post-img .post-img-labels .post-img-nsfw-label {
		font-size: 1em;
		color: var(--color-text-ban);
		border: 1px solid var(--color-text-ban);
		background: var(--color-back-dark);
	}
	.post-img.post-img-nsfw .post-img-thumbnail {
		opacity: 0.1;
		filter: blur(8px) grayscale(80%) contrast(50%);
		transition: 1.5s cubic-bezier(0, 0.75, 0, 1) !important;
		-moz-transition: 1.5s cubic-bezier(0, 1, 0, 1)!important;
	}
	.post-img.post-img-nsfw .post-img-thumbnail:hover {
		opacity: 0.4;
		filter: blur(3px) grayscale(0%) contrast(100%);
	}

	/* флажки прикрепленных и закрытых тредов */
	span.post-thread-options i.active {
		color: var(--color-green) !important;
	}

	/* ветка ОП-поста в древовидном режиме */
	.threads > div > div > .thread-tree > .thread-reply-chain:first-of-type {
		border: unset;
	}

	/* "Пропущено N постов" */
	.thread > div > div:nth-child(2) span {
		color: var(--color-text);
	}

	/* скрываемая панель тредов справа */
	.threads-scroll-toggler {
		background: var(--color-green) !important;
		width: 15px;
	}

	.threads-scroll-toggler > i {
		font-size: 22px;
		opacity: 1 !important;
	}

	/* окошко капчи */
	.captcha-form-wrapper {
		background: rgba(10, 30, 40, 0.6) !important;
	}

	.captcha-form {
		color: var(--color-text);
		background: var(--color-posts);
		width: 220px;
		height: 160px;
	}

	.captcha-form .captcha-close-btn {
		margin-right: 5px;
	}

	.captcha-form .separator {
		border-top: 1px solid var(--color-back);
		border-bottom: 1px solid var(--color-back);
	}

	.captcha-form form > input {
		margin: 0 0 10px 10px !important;
	}

	.captcha-form form > button {
		margin: 0 10px 10px 0 !important;
	}

	.captcha-form-wrapper > div > img {
		filter: invert(88%) contrast(300%);
	}

	/* полноразмерная картинка */
	.post-img-full {
		width: 100%
	}

	.post-embed-overlay {
		cursor: pointer;
	}

	.post-img .post-img-labels .post-img-gif-label {
		color: var(--color-green);
		border: 1px solid var(--color-green);
		background: var(--color-back)
	}

	/* скроллбар во фрейме */
	.sidemenu-boards-list {
		direction: rtl;
		right: -8px !important;
	}

	.sidemenu-boards-list * {
		direction: ltr;
	}


	/* гринтекст цитирования > */
	blockquote {
		color: var(--color-text-green) !important;
	}

	/* бан */
	.user-was-banned {
		color: var(--color-text-ban) !important;
	}

	/* ссылки на разделы на панели слева */
	.sidemenu-menu-top a:before,
	.sidemenu-menu-btm a ::before,
	.sidemenu-menu-top span,
	.sidemenu-menu-btm span {
		color: var(--color-green) !important;
	}

	.sidemenu-menu-top a:hover:before,
	.sidemenu-menu-btm a:hover ::before,
	.sidemenu-menu-top span:hover,
	.sidemenu-menu-btm span:hover {
		color: var(--color-green-dark) !important;
	}


	/* доски */
	a[href$="/b"] > span.sidemenu-board-dir:before {
		content: "Барак ";
	}

	a[href$="/it"] > span.sidemenu-board-dir:before {
		content: "Программисты-анальники ";
	}
	a[href$="/lovesis"] > span.sidemenu-board-dir:before {
		content: "Педо-инцест ";
	}
	a[href$="/ma"] > span.sidemenu-board-dir:before {
		content: "Пиздоглазые комиксы ";
	}
	a[href$="/mod"] > span.sidemenu-board-dir:before {
		content: "Обсуждение мочи ";
	}

	a[href$="/trains"] > span.sidemenu-board-dir:before {
		content: "Пёзда ";
	}

	a[href$="/n"] > span.sidemenu-board-dir:before {
		content: "Бомжевание ";
	}
	a[href$="/fiz"] > span.sidemenu-board-dir:before {
		content: "Гачимучи ";
	}
	a[href$="/pol"] > span.sidemenu-board-dir:before {
		content: "Пораша ";
	}
	a[href$="/mc"] > span.sidemenu-board-dir:before {
		content: "MeinKampf ";
	}
	a[href$="/a"] > span.sidemenu-board-dir:before {
		content: "Мультики для даунов ";
	}
	a[href$="/vn"] > span.sidemenu-board-dir:before {
		content: "Пиздоглазые игры  ";
	}
	a[href$="/vg"] > span.sidemenu-board-dir:before {
		content: "Гойминг  ";
	}

	a[href$="/crypt"] > span.sidemenu-board-dir:before {
		content: "Криптошизики  ";
	}
	a[href$="/1chan"] > span.sidemenu-board-dir:before {
		content: "Опущенки  ";
	}
	a[href$="/u"] > span.sidemenu-board-dir:before {
		content: "Утюги фото скачать ";
	}
	a[href$="/dreams"] > span.sidemenu-board-dir:before {
		content: "Ночные поллюции ";
	}
	a[href$="/tv"] > span.sidemenu-board-dir:before {
		content: "Семейный просмотр ";
	}
	a[href$="/gay"] > span.sidemenu-board-dir:before {
		content: "Мужики ";
	}
	/* меню */
	.menu-item > a .hidden-xs {
		display: none;
	}
	.menu-item > a[href="/"]:after {
		content: "Лента";
	}
	.menu-item > a[href$="/💚"]:after {
		content: "Подбор_очка";
	}
