	/* скроллбар всего сайта глобально */
	::-webkit-scrollbar,
	::-webkit-scrollbar-thumb,
	::-webkit-scrollbar-track {
		width: 5px!important;
		background: var(--color-green-dark) !important;
		border-radius: 10px;
	}
	::-webkit-scrollbar-button,
	::-webkit-scrollbar-corner,
	::-webkit-scrollbar-track-piece {
		background: var(--color-green-dark-dark)!important;
		height: 5px!important;
		border-radius: 10px;
	}


	/* ОТМЕЧЕННЫЕ ТРЕДЫ */
	/* белая тень шрифта - минус*/
	.empty-page {
		text-shadow: unset !important;
	}

	/* СООБЩЕНИЯ */
	.btn-default {
		color: var(--color-text)!important;
		background-color: #00000024 !important;
		border: none;
	}

	.form-control {
		background: var(--color-back) !important;
		color: var(--color-text) !important;
		border: 1px solid var(--color-posts);
	}


	.im-box .list-group-item {
		background: var(--color-posts) !important;
		color: var(--color-text) !important;
		border: 1px solid var(--color-back);
	}

	.dialog-footer,
	.dialog-header,
	.dialog.panel.panel-default {
		background: var(--color-posts) !important;
		color: var(--color-text) !important;
		border: unset !important;
	}

	.dialog-body,
	.im-message {
		color: var(--color-text) !important;
		background: var(--color-back) !important;
	}
	.im-box .dialog-view {
		background: unset !important;
	}

	.im-separator {
		border-top: 1px solid var(--color-posts) !important;
	}
	.im-box .list-group-item:hover {
		background: var(--color-hover) !important;
	}

	/* УПРАВЛЕНИЕ И МОДЕРАЦИЯ */
	.panel.panel-default {
		background: var(--color-back) !important;
		color: var(--color-text) !important;
		border: 1px solid var(--color-posts) !important;
	}
	.panel-footer {
		background: var(--color-posts) !important;
		color: var(--color-text) !important;
		border-top: 1px solid var(--color-posts) !important;
	}

	.panel-heading {
		background: var(--color-posts) !important;
		border-bottom: unset !important;
	}

	ul.pagination li a {
		background: var(--color-green-dark) !important;
		color: var(--color-text-white) !important;
		border: unset !important;
	}

	ul.pagination li.active a {
		background: var(--color-green-dark-dark) !important;
		border: 1px solid var(--color-green) !important;
		margin-top: -1px;
		/* граница */
	}

	/* запрет изменения ширины textarea в настройках чтобы верстка не ехала */
	#content > div > div:nth-child(2) > div > div:nth-child(4) > form > div > div.panel-body > div > textarea {
		max-width: 494px
	}

	/* ВХОД*/
	input {
		background: var(--color-back) !important;
		border: 1px solid var(--color-posts) !important;
		color: var(--color-text) !important;
	}

	input:hover {
		border: 1px solid #3F424F !important;
	}

	input:focus {
		border: 1px solid var(--color-green) !important;
	}


	/* === ФИСКСЫ БРАУЗЕРНЫХ ОСОБЕНОСТЕЙ === */
