style.css:
@-moz-document url-prefix('chrome://') {

toolbarbutton.bookmark-item > label[class="toolbarbutton-text"]:not([value="Linux"]),
toolbarbutton.bookmark-item .toolbarbutton-menu-dropmarker { display: none !important; }

toolbarbutton.bookmark-item:hover > label[class="toolbarbutton-text"],
toolbarbutton.bookmark-item:hover .toolbarbutton-menu-dropmarker { display: -moz-box !important; }

}
