style.css:
@-moz-document domain("hltv.org") {
:root {
    --default-color: #a3aeb3;
    --default-color-lighter: #b9bdbf;
    --default-border-color: rgba(255,255,255,.075);
    --border-color-light: rgba(127,127,127,.15);
    --border-color-dark: rgba(127,127,127,.65);
    --lost-color: #e02f2f;
    --a-default: #87a3bf;
    --default-box-shadow: 0 2px 5px 0 rgba(0,0,0,.25);
}

/* -----------------------
--- NAVIGATION (Full)
-------------------------- */

.navbar {
    box-shadow: var(--default-box-shadow);
}

.navbar .navcon {
    max-width: 1000px;
}

.navbar .navnews {
    border-left: none;
}

.navbar .nav, .navbar .navburger, .navbar .navevents, .navbar .navfantasy, .navbar .navforums, .navbar .navgalleries, .navbar .navlive, .navbar .navmatches, .navbar .navnews, .navbar .navranking, .navbar .navresults, .navbar .navstats {
	border-right: 1px solid rgba(127,127,127,.15);
    transition: .5s ease-out;
    font-size: 13px;
    padding: 0 12px;
}

.navbar .nav:hover, .navbar .navburger:hover, .navbar .navevents:hover, .navbar .navfantasy:hover, .navbar .navforums:hover, .navbar .navgalleries:hover, .navbar .navlive:hover, .navbar .navmatches:hover, .navbar .navnews:hover, .navbar .navranking:hover, .navbar .navresults:hover, .navbar .navstats:hover {
    background-color: rgba(127,127,127,.1);
}

.navbar .navsearch {
    margin: 0 10px;
}

/* - NAV POPUP
-------------------------- */

.navbar .nav-popup-header {
    text-transform: uppercase;
}

.navbar .nav-popup-header a {
    text-transform: initial;
}

.navbar .nav-popup-elm {
    font-weight: 600;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 5px 8px;
}

.navbar .nav-popup-elm .timezoneSelector {
    height: 26px;
}

/* - SEARCH BAR
-------------------------- */

.navbar .navsearchinput {
    background-color: rgba(0,0,0,.1);
    height: 30px;
    padding-left: 8px;
}

.navbar .navsearchborder {
    display: none;
}

.navbar .navsearchicon {
    top: 0;
    right: 0;
    padding: 2px 0 5px;
    width: 36px;
}

.navbar .navborder:before {
    border-left-color: rgba(127,127,127,.15);
    height: 46px;
    margin: 0;
}

/* -----------------------
--- COLUMN: TODAY'S MATCHES
-------------------------- */

.livescore .trailing {
    color: var(--lost-color);
}

.livescore .trailing,
.livescore .leading {
    font-weight: 700;
}

.teambox .middleExtra {
	color: #aabfc7;
    padding-left: 5px;
}

.leftCol .hotmatch-box .teambox, .right2Col .hotmatch-box .teambox, .rightCol .hotmatch-box .teambox {
    border-bottom-color: var(--border-color-light);
}

.teambox .teamrows {
    font-size: 11px;
    font-weight: 600;
    padding-right: 3px;
}

/* -----------------------
--- COLUMN: RECENT ACTIVITY
-------------------------- */

.activitylist .topic {
    margin-right: 5px;
}

.activitylist .matchCat {
    box-shadow: inset 3px 0 0 0 #e63865;
}
.activitylist .csCat {
    box-shadow: inset 3px 0 0 0 #ffae00;
}
.activitylist .newsCat {
    box-shadow: inset 3px 0 0 0 #55a528;
}

.activitylist .hltvCat {
    box-shadow: inset 3px 0 0 0 #3d6ea0;
}

.recent-activity .pro-mode-toggle {
    margin-top: 3px;
}

/* -----------------------
--- COLUMN: RESULTS
-------------------------- */

.rightCol .eventname {
    font-size: 10px;
    padding: 2px 6px;
}

.rightCol .eventname:hover {
    text-decoration: underline;
}

.teambox .twoRowExtraRow {
    font-weight: 700;
}

.teambox .middleExtra {
    font-size: 10px;
}

/* -----------------------
--- STATS
-------------------------- */

.stats-section .stats-top-menu .tabs .tab-title {
	position: initial;
    display: inline-block;
    font-size: 10px;
    margin: 5px 8px 0;
}

.stats-section .stats-top-menu .tabs .stats-sub-navigation-title {
    margin-right: 10px;
}

.stats-section .stats-top-menu .tabs select,
.stats-section .stats-top-menu .tabs .stats-sub-navigation-simple-filter-map {
    margin: 2px 0 5px 5px;
}

.stats-section .stats-top-menu .tabs .stats-sub-navigation-simple-filter-map-hover {
    padding: 8px;
}

.stats-section .stats-top-menu .tabs .stats-sub-navigation-simple-filter-map-hover .g-grid {
    margin-left: 0;
}

.stats-section .stats-top-menu .tabs .stats-sub-navigation-simple-filter-map-hover .g-grid .col-4 {
    padding-left: 0;
    padding-bottom: 2px;
}

.stats-section .stats-top-menu .tabs .stats-sub-map a {
    box-sizing: border-box;
    display: inline-block;
    width: 100%;
}

.stats-section .stats-rows .stats-row>span:first-child.status-indicator.worst {
    box-shadow: inset 3px 0 0 0 #e02f2f;
}

.stats-section .stats-rows .stats-row>span:nth-child(2).status-indicator.worst {
    box-shadow: inset -3px 0 0 0 #e02f2f
}

/* - STATS TABLE
-------------------------- */

.stats-table tr:hover .lost,
.stats-table .lost {
    color: var(--lost-color);
}

.stats-table .ratingNegative {
    color: var(--lost-color);
}

/* -----------------------
--- RESULTS
-------------------------- */

.results h1.standard-headline {
    font-size: 22px;
    font-weight: 700;
}

.results .standard-headline {
    font-size: 14px;
    font-weight: 600;
}

.results .results-holder .score-lost {
    color: var(--lost-color);
}

.results .results-holder .team-cell .team-won {
/*     color: var(--default-color-lighter); */
}

/* -----------------------
--- EVENTS
-------------------------- */

.event-header-component .headline {
    font-weight: 700;
    text-transform: uppercase;
}

.events-page .event-status-upcoming-headline {
    font-size: 20px;
    opacity: initial;
}

.events-page .events-month .standard-headline {
    font-size: 16px;
    margin: 16px 0 10px 2px;
    font-weight: 600;
}

.event-page .teams-attending .team-box .lineup-box {
    font-weight: 600;
}

.event-page .placements-holder {
    padding: 6px;
}

.event-page .placements .placement {
    box-shadow: var(--default-box-shadow);
}


.slotted-bracket .round .slots .slot-wrapper .match .loser {
    border-left-color: var(--lost-color);
}

.slotted-bracket .round .slots .slot-wrapper .match .live-score, .slotted-bracket .round .slots .slot-wrapper .match .result {
    font-weight: 700;
}

.slotted-bracket .round .slots .slot-wrapper .match .live-score .current-map-score.trailing, .slotted-bracket .round .slots .slot-wrapper .match .live-score .total-map-score.trailing {
    color: var(--lost-color);
}

/* - STATS
-------------------------- */

.stats-section .top-x-box .average, .stats-section .top-x-box .rating .bold, .stats-section .top-x-box .average, .stats-section .top-x-box .average .bold {
    font-size: 12px;
}

.stats-section .top-x-box .average, .stats-section .top-x-box .rating, .stats-section .top-x-box .average, .stats-section .top-x-box .average {
    font-size: 10px;
}

/* -----------------------
--- FORUM
-------------------------- */

.standard-box {
    box-shadow: var(--default-box-shadow);
}

.standard-headline {
    line-height: 22px;
}

.forumthread .topic {
    font-size: 14px;
}

.forumthread .forum-middle,
.forum .forum-middle {
    line-height: 23px;
}

.forumthread .fan-con {
    font-size: 0;
    border-right-color: var(--border-color-light);
    margin-right: 6px;
    padding-right: 6px;
}

.forumthread .forum-bottombar,
.forum .forum-bottombar {
    border-top-color: var(--border-color-light);
}

.forumthread .forum-action-button {
    margin-top: 2px;
    padding: 1px 8px;
}

.forumthread .edit-button, .forumthread .reply-button, .forumthread .report-button,
.forum .edit-button, .forum .reply-button, .forum .report-button {
    padding: 0 10px;
    border-left-color: var(--border-color-light);
}

.forumthread .edit-button, .forumthread .reply-button, .forum .edit-button, .forum .reply-button {
    padding: 0 18px;
}

.forumthread .reply-button .fa, .forum .reply-button .fa {
    font-size: 11px;
    margin-right: 2px;
}

.forumthread .report-button,
.forum .report-button {
    color: inherit;
    opacity: 0.35;
}

.forumthread .report-button:hover,
.forum .report-button:hover {
    opacity: 0.75;
}

.forumthread .time, .forum .time {
    font-size: 11px;
    font-style: italic;
}

/* - FORUM POSTING
-------------------------- */

.forumpostthread > .standard-headline {
    color: #929a9e;
    font-size: 13px;
    font-weight: 700;
    text-transform: capitalize;
}

.forumpostthread .standard-box .standard-headline {
    color: inherit;
}

.forumpostthread .width100,
.forumpostthread select.width100 {
    font-size: 13px;
    line-height: 23px;
    padding: 4px 9px;
    text-align: justify;
}

.forumpostthread select.width100 {
	padding: 5px 9px 6px;   
}

.forumpostthread textarea.width100 {
	height: 250px;
}


/* -----------------------
--- NEWS
-------------------------- */

.index .newstext {
    font-size: 14px;
    font-weight: 600;
}

.index .newstc {
    line-height: 12px;
}

.index .newsrecent {
    opacity: 1;
    margin-bottom: 5px;
    color: rgba(127,127,127,.65);
    font-style: italic;
}

.index .newsline+.newsline {
    border-top-color: var(--border-color-light);
}

.newsitem .article-info {
    margin: 10px 0;
}

.newsitem .article-info .date {
/*     color: rgba(255,255,255,.35); */
    font-style: italic;
    letter-spacing: 0.5px;
}

.news-block {
/*     color: #b9bdbf; */
    line-height: 2em;
    margin: 1.5em 0;
    text-align: justify;
}

.newsdsl .image-con {
    margin: 1.5em 0;
}

.newsdsl .imagetext {
    margin-top: 5px;
    font-style: italic;
}

.newsdsl .videoCon {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.25);
}

/* -----------------------
--- EVENT HUB
-------------------------- */

.index .space-between-eventheader-hero {
    margin-top: 16px;
}

.event-hub {
    box-shadow: var(--default-box-shadow);
}

.event-hub .event-hub-indicator.event-live {
    background: var(--lost-color);
}

.event-hub .event-hub-link {
/*     color: var(--default-color); */
    border-color: var(--default-border-color);
    color: #929a9e;
    font-size: 13px;
    font-weight: 600;
}

.event-hub .event-hub-indicator.event-ended {
    background: rgba(255,255,255,.75);
    color: #232729;
}

/* -----------------------
--- MATCH PAGE
-------------------------- */

.match-page .teamsBox .timeAndEvent .event {
	font-size: 13px;
    font-weight: 600;
}

.match-page .teamsBox .timeAndEvent .date {
    font-size: 12px;
    font-style: italic;
    letter-spacing: 0.15px;
}

.match-page .teamName {
    font-size: 18px;
    font-weight: 500;
    text-shadow: 1px 1px 10px rgba(0, 0, 0, .25);
}

.match-page .teamsBox .timeAndEvent .event:hover {
    text-decoration: underline;
}

.match-page .teamsBox .timeAndEvent .countdown {
    font-size: 14px;
    font-weight: 600;
	letter-spacing: 0.5px;
}

.match-page .lost {
    color: var(--lost-color);
    font-weight: bold;
}

.match-page .maps .veto-box {
    font-size: 13px;
    line-height: 23px;
}

.match-page span.headline {
    display: inline-block;
    margin-bottom: 5px;
}

/* - SCORE BOT
-------------------------- */

.scorebot .topbarBg .currentRoundText {
    color: white;
}

.scorebot .scoreboard .ctTeamHeaderBg {
    background-color: rgba(32,128,195,.65);
}
.scorebot .scoreboard .ctPlayerBg {
    background-color: rgba(20,93,144,.6);
}

.scorebot .scoreboard .tTeamHeaderBg {
    background-color: rgba(247,186,22,.65);
}

.scorebot .scoreboard .tPlayerBg {
    background-color: rgba(179,133,10,.7);
}

.scorebot .scoreboard .teamName {
    font-size: 13px;
}

.scorebot .scoreboard .standard-box {
    font-family: monospace;
}

.scorebot .scoreboard .nameCell {
    font-weight: 500;
}

/* -----------------------
--- GALLERIES
-------------------------- */

.galleries .gallery-name {
    font-size: 16px;
    line-height: 20px;
    padding: 8px;
    text-shadow: 0 1px 10px #000;
}

.galleries .gallery-day {
    font-size: 12px;
    font-style: italic;
}

/* -----------------------
--- RANKING
-------------------------- */

.ranking .ranked-team .ranking-header .negative {
    color: var(--lost-color);
}

.ranking .ranked-team .ranking-header .position {
    margin-right: 10px;
    font-weight: 600;
    font-size: 14px;
    width: 20px;
    color: rgba(255,255,255,.5);
}

.ranking .ranked-team .ranking-header .positive, 
.ranking .ranked-team .ranking-header .negative {
    font-weight: 600;
}

 .ranking .ranked-team .player-holder:hover {
    background-color: rgba(127,127,127,.2);
}

.ranking .ranked-team .lineup {
    border-spacing: 5px 0;
    padding-top: 5px;
}
.ranking .ranked-team .player-holder{
    width: calc(20% - 10px);
    padding: 5px;
}

.ranking .ranked-team .nick {
    padding: 3px 0 0;
    font-weight: 600;
}

.ranking .ranked-team .more {
    border-top: 1px solid rgba(127,127,127,.15);
    margin-top: 5px;
}

.ranking .region-selector .ranking-region {
    font-weight: 600;
    font-size: 13px;
}

.ranking .region-selector a:hover {
    text-decoration: underline;
}

.ranking .region-selector .region {
    margin-top: 10px;
    line-height: 23px;
}
.ranking .region-selector .region .ranking-country {
    font-size: 13px;
}

/* -----------------------
--- MATCHES
-------------------------- */

.upcomingMatchesSection .matchTeam {
    font-weight: 600;
}

.upcomingMatchesSection .matchEvent .matchEventName {
	color: rgba(146,154,158,.75);
}

/* -----------------------
--- USER PROFILE
-------------------------- */

.user-profile-settings .headline {
    margin-bottom: 7px;
}

/* -----------------------
--- COLUMNS
-------------------------- */

.leftCol h1, .right2Col h1, .rightCol h1 {
    padding: 4px 0 0;
}

.leftCol, .right2Col, .rightCol {
    font-weight: 600;
}

.col-box {
    border-top-color: var(--border-color-light);
}

.leftCol>*, .right2Col>*, .rightCol>* {
    margin-bottom: 10px;
}

/* -----------------------
--- GLOBAL
-------------------------- */

body {
/*     color: var(--default-color); */
}

.button {
    box-shadow: var(--default-box-shadow);
/*     color: var(--default-color-lighter); */
}

.lost {
    color: var(--lost-color);
}

/* - FOOTER
-------------------------- */

.footer .footerlinks span {
    flex-grow: 1;
    font-weight: 600;
    height: 100%;
}

.footer .footerlinks span a:not(.footerlink.socicon) {
    display: inline-block;
    width: 100%;
}

.footer .footerlinks span a,
.footer .footerlinks span span {
    line-height: 73px;
}

.footer .footer-responsible-container {
    margin-top: 0;
}
}
