style.css:
@-moz-document domain("lab.meneame.net") {
@media (min-width:959px){
/* Cambio de color de cabecera */
header{background-color:#e35624 !important; color:#fff !important}
header h1 img.mobile-hidden{display:none !important}
header .container{width:100% !important}
header .header-left + div *{color:#fff !important}
header .header-left + div .options-container *{color:#666 !important}
header .header-left + div .buttons-horizontal:hover *{color:#e35624 !important}
header .action{color:#fff !important}
header .container > .header-left{padding-left:30px !important; padding-right:30px !important}
header .input-light input, header .input-light input::placeholder{color:#fff !important}
header .input-light input, header .input-light input:focus::placeholder{color:#000 !important}
header .input-light input{color:#fff !important}
header .input-light input:focus{color:#000 !important}
header .input-light svg{-webkit-filter:invert(100%); filter:invert(100%)}


#main-logo{background:url(https://www.meneame.net/img/mnm/logo-white.svg) no-repeat !important; height:30px !important; width:163px !important}
#main-logo .mobile-hidden{display:none !important}

/* Columnas principales más anchas */
#main{margin-left:auto !important; margin-right:auto !important; padding:0 30px; max-width:1200px}
#main > #center{width:auto !important}
#main > #right-sidebar{width:auto !important}

/* Tamaño fuentes */
.link .link-header a{font-size:18px}

/* Añade sombreado a las cajas */
.ad-container{display:none !important; margin:0 !important}
.advises-container, div.link, .sidebar-preguntame, .sidebar-list, .featured-community, .tabs.feed-menu{box-shadow:0 0 18px 0 rgba(0,0,0,.1) !important}

/* Cambiar botón de meneos */
#tab-main .link{padding-left:100px; position:relative}
#tab-main .link > div.footer{position:initial}
#tab-main .link .footer{padding-left:20px !important}
#tab-main .link .info a:first-of-type{color:#e35614}
#tab-main .link .info a:last-of-type{color:#000}
#tab-main .button-vote{background-color:#e35614; background-image:linear-gradient(-180deg, #f5720e 0%, #fe4a00 100%); border:1px solid rgba(122, 47, 11, 0.45); border-radius:10px !important; font-size:16px !important; height:auto !important; left:0; line-height:1.5em !important; margin:0 !important; padding:12px 10px 10px 10px !important; position:absolute; top:0; min-width:80px !important; width:100px !important; z-index:9}



/* Márgenes y paddings */
#main{margin-top:90px !important}
#tab-main .link .body.horizontal{padding-top:5px !important}

/* Ajuste alto imagen feedback */
#feedback_advise img{height:100% !important}
#feedback_advise .advise-image{height:100% !important}

}
}
