style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://apps.facebook.com/petsociety") {
	.UIStandardFrame_Container {
		width: 500px !important;
		padding-left: 0 !important;
	}
	.UIStandardFrame_SidebarAds,
	.app_content_11609831134 div#app11609831134_sidebar,
	.app_content_11609831134 style+script+div,
	.app_content_11609831134 p{
		display:none !important;
	}
	.UIStandardFrame_Content {
		width: 500px !important;
	}
	.app_content_11609831134 div#app11609831134_sidebar + div,
	.app_content_11609831134 div#app11609831134_sidebar + div iframe{
		width: 500px !important;
		height: 510px !important;
	}
}

@-moz-document url-prefix("http://pets.game.playfish.com/g/fb/pets") {
	body {
		margin: 0 !important;
	}
	embed {
		width: 500px !important;
		height: 510px !important;
	}
}
