style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://apps.facebook.com/crazyplanets") {
	.UIStandardFrame_Container {
		width: 500px !important;
		padding-left: 0 !important;
	}
	.UIStandardFrame_SidebarAds,
	.app_content_48596151436 div#app48596151436_sidebar,
	.app_content_48596151436 style+script+div,
	.app_content_48596151436 p{
		display:none !important;
	}
	.UIStandardFrame_Content {
		width: 500px !important;
	}
	.app_content_48596151436 div#app48596151436_sidebar + div,
	.app_content_48596151436 div#app48596151436_sidebar + div iframe{
		width: 500px !important;
		height: 550px !important;
	}
}

@-moz-document url-prefix("http://planets.game.playfish.com/g/fb/planets") {
	body {
		margin: 0 !important;
	}
	embed {
		width: 500px !important;
		height: 550px !important;
	}
}
