style.css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }
/*---------------------------------------- BACKGROUND ----------------------------------------*/
  .card--grit
  {
    background-image: url("https://media.discordapp.net/attachments/870099227395625000/870396022378074152/20210729_Kraken_Background.png?width=696&height=391");
    background: fixed;
  }

  html
  {
    background: fixed url("https://media.discordapp.net/attachments/870099227395625000/870396022378074152/20210729_Kraken_Background.png?width=696&height=391") no-repeat, #fff;
    box-sizing: border-box;
    color: #fff;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}

  .btn--primary
  {
    background: #fff;
    color: #fff;
  }
/* TITLE */
  .theme--pDefault .profile-title 
  {
    color: #000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
/* LEVEL NUMBER */
  .theme--pDefault .profile-levelNum
  {
    color: #000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .tc-t {
    color: #000;
  }
/* CASH */
  .theme--pDefault .profile-body
  {
    color: #000;
  }
}

@-moz-document url-prefix("https://www.nitrotype.com") {
/*---------------------------------------- PROFILE BACKGROUND ----------------------------------------*/
.theme--pDefault .profile-bg
{
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg,#fff 60%, #fff 100%);
    color: #fff;
    background-size: 100%
}

.theme--pDefault .profile-bgHead
{
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg,#fff 60%, #fff 100%);
    color: #fff;
    background-size: 100%
}
/* GOLD MEMBER*/
.theme--pDefault.is-gold .profile-bg
{
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg,#ffec00 60%, #fff 100%);
    color: #feff00;
    background-size: 100%
}
.card--a
{
    background-color: #fff0;
    color: #000;
}

.dash-copy
  {
    color: #a603fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }
.dash-copyContainer
  {
      background: #000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }
/* RACE TEXT FONTS */
  .dash-copy
  {
    color: #000;
    font-family: "Indie Flower", "Bad Script", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #000;
    color: #000;
  }

  .bg--gradient {
    background: fixed;
  }
/* PROFILE BACKGROUND */
  .theme--pDefault .profile-bg
  {
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
    color: #000;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #000;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }
/* CLICKING STUFF */
  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
.btn--primary {
    background-image: url("https://i.pinimg.com/originals/c8/7a/52/c87a52f38c32f8b3102f3df4677cb215.jpg");
    color: #000;
    background-size: 100%;
    background-color: #000 !important;
   
}
.btn--secondary {
    background-image: url("https://i.pinimg.com/originals/c8/7a/52/c87a52f38c32f8b3102f3df4677cb215.jpg");
    color: #000;
    background-size: 100%;
        background-color: #000 !important;
/* PROFILE BACKGROUND */
}
.growl--a {
    background-image: url("https://images.unsplash.com/photo-1557682224-5b8590cd9ec5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8OHx8fGVufDB8fHx8&w=1000&q=80");
    border-color: rgba(2, 2, 2, 0.2);
    background-size: 100%;
    color: #000;
}
/* RACE LOGS */
path:first-child, path:nth-child(7) {
    fill: #264f76;
}
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}
.raceResults-awards {background:#fff0;}
.raceResults-close.raceResults-close--minimizer {background:#dfd612;}
.g.raceResults-grid {background:#fff0;}
/* RACE TEXT */
.dash-content {background:url("https://images.pexels.com/videos/3045163/free-video-3045163.jpg?auto=compress&cs=tinysrgb&dpr=1&w=500");}
/* BOTTOM CHOICE */
.footer-nav
{    
background:url(https://media.discordapp.net/attachments/870099227395625000/870393751871311972/20210729_Kraken.png?width=696&height=392)
}
/* BOTTOM BOTTOM */
.footer-copyright
{
   background:url(https://media.discordapp.net/attachments/870099227395625000/870393751871311972/20210729_Kraken.png?width=696&height=392)
}
/* LIST */
.nav-list
{   
    background:url(https://png.pngtree.com/thumb_back/fw800/back_our/20190622/ourmid/pngtree-business-atmosphere-brushed-shading-blue-gradient-background-image_218331.jpg)
}
}
