@-moz-document domain("www.pixiv.net") {

/* Title */
#header_top + div,
/* Ads */
.ads_area,
.floleft[style="font-weight: bold;"],
.floleft[style="font-weight: bold;"]+div,
.floleft[style="font-weight: bold;"]+div+div,
#content3 > style + div[style="width: 100%;"],
iframe[src^="http://ads"],
iframe[name="maad"]
  { display: none !important; }

#header_top
  { margin-bottom: 0 !important; }

#navi
  { margin-top: 3px !important; }

}
