/* Barre de lecture */

/* Video progress bar */
 .html5-play-progress, .ytp-play-progress {
    background: #5099FF;
    background: -moz-linear-gradient(top, #5099FF 0%, #5099FF 35%, #ffffff 65%, #5099FF 100%);
    background: -webkit-linear-gradient(top, #5099FF 0%, #ffffff 35%, #ffffff 65%, #5099FF 100%);
    background: linear-gradient(to bottom, #5099FF 0%, #ffffff 24%, #ffffff 65%, #5099FF 100%);
    box-shadow: 0px 0px 20px #5099FF, 0px 0px 20px #5099FF, 0px 0px 20px #5099FF !important;
}
/* Barre de chargement */

/* Video load bar */
 .html5-load-progress, .ytp-load-progress {
    background: #70B9FF;
    background: -moz-linear-gradient(top, rgba(120, 120, 120, 0.3) 0%, rgba(120, 120, 120, 0.3) 45%, rgba(255, 255, 255, 0.3) 55%, rgba(120, 120, 120, 0.3) 100%);
    background: -webkit-linear-gradient(top, rgba(120, 120, 120, 0.3) 0%, rgba(255, 255, 255, 0.3) 45%, rgba(255, 255, 255, 0.3) 55%, rgba(120, 120, 120, 0.3) 100%);
    background: linear-gradient(to bottom, rgba(120, 120, 120, 0.3) 0%, rgba(255, 255, 255, 0.3) 40%, rgba(255, 255, 255, 0.3) 55%, rgba(120, 120, 120, 0.3) 100%);
    box-shadow: 0px 0px 12px rgba(120, 120, 120, 0.3), 0px 0px 12px rgba(120, 120, 120, 0.3), 0px 0px 12px rgba(120, 120, 120, 0.3) !important;
}
/* Bouton de scroll */

/* Scrubber button */
 .html5-scrubber-button, .ytp-scrubber-button {
    background: rgba(255, 255, 255, 0) !important;
    border-color: rgba(255, 255, 255, 0) !important;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0), 0px 0px 0px rgba(255, 255, 255, 0), 0px 0px 0px rgba(255, 255, 255, 0) !important;
    transition: box-shadow 0.0s, background 0.0s !important;
}
/* Boutton de scroll ÃÂÃÂ¢ÃÂÃÂÃÂÃÂ hover */

/* Scrubber button ÃÂÃÂ¢ÃÂÃÂÃÂÃÂ hover */
 .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
    background: #FFFFFF !important;
    background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #5099FF 100%) !important;
    background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #5099FF 100%) !important;
    background: radial-gradient(ellipse at center, #ffffff 0%, #ffffff 20%, #5099FF 100%) !important;
    border-color: #ffffff !important;
    box-shadow: 0px 0px 10px #5099FF, 0px 0px 12px #5099FF, 0px 0px 14px #5099FF !important;
}
/* RÃÂÃÂÃÂÃÂ©glage volume /    / Volume control bar */
 .ytp-volume-slider-handle {
    position:absolute;
    top:50%;
    width:3px;
    height:14px;
    margin-top:-7px;
    margin-left:0px;
    background: #ED0000 !important;
    background: -moz-linear-gradient(left, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%) !important;
    background: -webkit-linear-gradient(left, #ED0000 0%, #ffffff 35%, #ffffff 65%, #ED0000 100%) !important;
    background: linear-gradient(to right, #ED0000 0%, #ffffff 24%, #ffffff 65%, #ED0000 100%) !important;
    box-shadow: 0px 0px 10px #ED0000, 0px 0px 10px #ED0000, 0px 0px 10px #ED0000 !important;
}
.ytp-big-mode .ytp-volume-slider-handle {
    width:4px;
    height:22px;
    margin-top:-11px
}
.ytp-volume-slider-handle:before, .ytp-volume-slider-handle:after {
    background: #ffffff !important;
    background: -moz-linear-gradient(top, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%) !important;
    background: -webkit-linear-gradient(top, #ED0000 0%, #ffffff 35%, #ffffff 65%, #ED0000 100%) !important;
    background: linear-gradient(to bottom, #ED0000 0%, #ffffff 24%, #ffffff 65%, #ED0000 100%) !important;
    /*   box-shadow: 0px 0px 5px #ED0000, 0px 0px 5px #ED0000, 0px 0px 5px #ED0000 !important;*/
    width:50px;
    margin-left:-50px;
}
.ytp-volume-slider-handle:after {
    left:0px;
    background:rgba(255, 255, 255, .2)
}
.ytp-big-mode .ytp-volume-slider-handle:before, .ytp-volume-slider-handle:after {
    width:50px;
    height:2.5px;
}
.ytp-big-mode .ytp-volume-slider-handle:after {
    left:-46px;
    background:rgba(255, 255, 255, .2)
}
/* Badge HD-4k-5k-8k-3D */
