/* Settings gear */
 .ytp-settings-button svg {
    -moz-transition:-moz-transform .4s cubic-bezier(.58, .24, .47, 2.3);
    -webkit-transition:-webkit-transform .4s cubic-bezier(.58, .24, .47, 1);
    -ms-transition:-ms-transform .4s cubic-bezier(.58, .24, .47, 2.3);
