style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://forum.solidworks.com/") {
body, input, select, textarea {
font-family:"Segoe UI",Tahoma,Verdana,Arial !important;
font-size: .8em !important;
}
h2 {
font-size: 1.4em !important;
}
.jive-table td.jive-table-cell-subject a {
font-size:1.0em !important;
}
#jive-wrapper {
width: 98% !important;
}
#jive-sub-wrapper, #jive-sub-sub-wrapper {
width: 100% !important;
}
#jive-wrapper, body {
background: #fff !important;
}
#jive-sub-sub-wrapper {
margin:10px !important;
}
#jive-body-maincol-container {
width: 100% !important;
float:none !important;
}
#jive-body-maincol {
margin:0 !important;
width: 100% !important;
}
#jive-body-sidebarcol-container, #jive-body-sidebarcol {
float: left !important;
width:100% !important;
}
.jive-sidebar {
width: 15% !important;
float:left !important;
margin:3px 1% !important;
}
#footer {
width: 100% !important;
color:black !important;
background: #fff !important;
}
#footer {
margin:10px !important;
border:1px solid black;
}
#footer p, #footer ul li, #footer ul li a {
color:black !important;
font-size:1.0em !important;
}
#footer p br {
display:none !important;
}
}
