style.css:
@-moz-document domain(2ch.hk) {
html, body {
	font-family: /*[[font]]*/; /*для стандартного шрифта удалите строку или вставьте название нужного вам*/
  background: /*[[backgroundColor]]*/;
	--text-color: /*[[mainColor]]*/; /*основной цвет*/
    --alt-color: /*[[secondColor]]*/; /*доп цвет. Должен быть темнее основного ~на 70 единиц. Обычно используется для затемнения букв когда наводишь курсор на ссылоку */
}

:root {
	--theme_default_title: var(--text-color);
	--theme_default_border: var(--text-color);
	--theme_default_text: var(--text-color);
	--theme_default_alttext: var(--text-color);
	--theme_default_btntext: var(--text-color);
	--theme_default_btnborder: var(--text-color);
  	--theme_default_drag: var(--text-color);
	--theme_default_link: var(--text-color);
    --theme_mubtn: var(--alt-color);
    --theme_mpborder: var(--alt-color);
	--theme_default_postbg: black;
    --theme_default_btnbghover: black;
    --theme_default_btnbg: black;
    --theme_default_altbtnbg:none; /*уберите чтобы сделать стрелки для прыжка по странице снова с белым фоном*/
    --theme_default_hover: var(--text-color); /*уберите чтобы сделать стрелки для прыжка по страничке и другие элементы снова с оранжевым фоном*/
	--theme_default_spoiler:#454545;
    
    /*темы каталога*/
    --theme_border: var(--text-color);
	--theme_blockbg: var(--alt-color);
    --theme_text: var(--text-color);
    --theme_inputtext: var(--text-color);
    --theme_logo_text: var(--text-color);
    
    /*темы на главной*/
    --theme_default_postbg2: var(--alt-color);
    
    /*темы для трекера*/
    --theme_inputbg: black;
}




/*замена цвета в каталоге*/
.ctlg__meta{
	color: var(--alt-color);
}
.pagetitle>a:hover, .linktoboard>a:hover{
    color: var(--text-color) !important;
    text-decoration: none;
    background-color: black;
}
.pagetitle>a, .linktoboard>a, .footer>a{
	color: var(--text-color);
    text-decoration: underline;
}
.ctlg__comment>a{
    text-decoration: underline;
}
.ctlg__comment>a:hover{
    color: var(--text-color);
}




/*замена цвета на главной*/
.th.board-title, .th.board-name, .th.board-speed, .th.board-ip, .th.board-desc, .th.board-posts{
	background: var(--text-color)!important;
    border: none !important;
}
.table{
    border-spacing: 0px;
}
.ui-widget-content, .summary__row, .main__block_wrapped, #tabbed li{
	background:black !important;
}
#filter{
	background-color: var(--text-color);
    border-color: var(--alt-color);
}
.buttons{
    display: None;
}




/*замена цвета на трекере*/
.header>.logo{
	color: var(--text-color);
}
.header>.logo:hover{
	color: var(--alt-color);
}
.box.thin.first{
	color: var(--text-color) !important;
    background-color: black !important;
    border: 1px solid var(--text-color);
}
.comment>a{
	text-decoration: underline;
}
#toggle-filter{
	background-color:black;
    color: var(--text-color);
    border: 1px solid var(--text-color);
}
.reply{
	color: #707070; /*поставьте var(--text-color) чтобы текст о информации треда был зелёным, а не серым*/
}





/*Отображение вашего поста*/
.post_type_watched{ 
	border-left-width: 5px;
}
/*Отображение нового поста в треде*/
.post_type_highlight{
  background-color: #151515;
}

::selection {
background: var(--alt-color);
}

/*удалите чтобы сделать рамку вокруг webm зелёной*/
.post__file-webm{
	border-color: gray;
}

.input {
	color: var(--text-color);
  	background: black;
	background-image: -webkit-gradient(linear,center top,center bottom,from(#000000));
}

.fm__header:hover{
  background-color: var(--alt-color); /*для белого цвета в левом меню выбора доски поставьте white*/;
}

#SwitchStyles>option:hover{
  background-color: var(--alt-color);
}
#SwitchStyles>option:hover{
  background-color: var(--alt-color);
}
#SwitchStyles{
	background-color: black;
}

a:hover{
	text-decoration:none;
    color: var(--alt-color);
}
a{
	text-decoration: underline;
    color: var(--text-color);
}





/*восстановление белых линий*/
.navbar{
    border-color: white;
	border-top-width: 1px;
	border-top-style: solid;
	border-bottom-width: 1px;
	border-bottom-style: solid;
    padding-top: 10px;
    padding-bottom: 10px;
}



/*восстановление скобочек в nav баре (там где обновить тред,)*/
.thread-nav__inner>a::after{
	content:']';
}
.thread-nav__inner>a::before{
	content:'[';
}



/*восстановление собочек у параметров файла*/
.post__filezise{
	position: relative;
    color: gray; /*убрите чтобьы текст сделать зелёным у параметра файла*/
}
.post__filezise::before{
	content: '(';
}
.post__filezise::after{
	content: ')';
}



/*восстановление "ответы" у оп-поста*/
.post_type_oppost>.post__refmap::before{
    font-style: italic;
    font-size: 100%;
    content:"Ответы: "
}



/*уборка точек у постов и добавление # к номеру поста*/
.post__number{
	position: relative;
}
.post__number::before{
	content: '#';
}
.post_type_reply{
	position: static;
}
.mmm, .search, #refmap-252352771{
  display: none;
}
/*ver 1.2.4*/
}
