	background-image: url(https://almaer.com/scrollbar/images/scroll_gutter_top.png), url(https://almaer.com/scrollbar/images/scroll_gutter_mid.png);
	background-repeat: no-repeat, repeat-y;
}
/* Bottom area below thumb and down button */
