style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.metafilter.com"), domain("metatalk.metafilter.com")
{
    /* Remove 'Add to Favorites' Plus Mark */
    #page span[id^='fav']
    {
        display:none !important;
    }
    
    /* Remove 'Recent Activity' Link from Header */
    #navoften > *:nth-child(4n)
    {
        display:none !important;
    }
    
    /* Remove 'My Comments' Tab */
    #maintab > *:nth-child(4n)
    {
        display:none !important;
    }
    
    /* Remove 'Recent Comments' Tab */
    #maintab > *:nth-child(5n)
    {
        display:none !important;
    }
    
    /* Remove "# New" Notes */
    a.new
    {
        display:none !important;
    }
    
    /* Remove Parantheses Around "# New" Notes */
    #page b[style='color: white;']
    {
        display:none !important;
    }
}
