#header-container #header {
    background-image: url(https://i.imgur.com/AMkbx9y.png) !important;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1030;
    display: table;
    width: 100%;
    height: 54px;
    padding: 0;
    line-height: 54px;
    color: #222;
    background-color: black;
    border-bottom: 1px solid #eee;
}
.story-details-page .story-list .story-list__container {
    background-image: url(https://i.imgur.com/AMkbx9y.png) !important;
}
.story-details-page .table-of-contents {
    background-image: url(https://i.imgur.com/AMkbx9y.png) !important;
}
.story-details-page .table-of-contents .table-of-contents__header {
    font-weight: 700;
    font-size: 22px;
    line-height: 28px;
    color: #121212;
    padding: 12px;
}
.wp-modal {
    background-image: url(https://i.imgur.com/AMkbx9y.png) !important;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, .5);
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    overflow: hidden;
    z-index: 1031;
}
.story-details-page .description .description-text {
    background-image: url(https://i.imgur.com/AMkbx9y.png) !important;
    font-family:"Source Sans Pro", "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: #121212;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 22px;
    text-shadow: 1px 1px #e397ac !important;
}
#header-container #header #header-item-search form button {
    position: absolute;
    top: 11px;
    left: 0;
    margin: 0;
    cursor: pointer;
    border: none;
    background: 0 0;
    padding: 7px 5px;
    -webkit-transition: color .1s ease;
    transition: color .1s ease;
}
#header-container #header>nav>ul>li#header-item-logo a {
    display: inline-block;
    width: 126px;
    height: 54px;
    vertical-align: middle;
    background: url(https://b.thumbs.redditmedia.com/GHyhGExEwbfnYpXzYU_UHEw6ImgnpEke--gcpqqNcYc.png);
    background-size: 100px;
    background-repeat: no-repeat;
    background-position-y: 5px;
}
@font-face {
    font-family: Font1;
    src: url('http://fonts.googleapis.com/css?family=Alegreya+Sans+SC');
}
