style.css:
@-moz-document domain("eldarya.fr"), domain("eldarya.com.br"), domain("eldarya.de"), domain("eldarya.es"), domain("eldarya.hu"), domain("eldarya.pl"), domain("eldarya.it"), domain("eldarya.ru"), domain("eldarya.com") {
/*GENERAL*/
#map-container .map-location-tooltip .energy-required, #map-container .map-location-tooltip .time-to-explore{
	left: -75px; top: 20px;
}
#map-location-tooltips .map-location-tooltip .tooltip-bg {
        display: none;
    }

#map-location-tooltips .map-location-tooltip .map-location-name {
        left: 0;
  }
.pet-interface-container #treasure-hunt-interface .lens {
  border: 1.5px solid rgba(61, 219, 255, .21);
  border-radius: 20px;
  background: rgba(255, 255, 255, .89);
  z-index: -1;
  margin-top: 0;
  }
#map-location-tooltips .map-location-tooltip .map-location-infos {
  box-shadow: none;
  left: 50px;
  top: 45%;
  border: none;
  background: rgba(255, 255, 255, 0);
    }

#map-location-tooltips .map-location-tooltip .map-location-history {
        left: -8px;
    }

div.map-location-tooltip:after {
        position: absolute;
        top: 37%;
        left: 20%;
        color: #000;
        font-family: "Alegreya Sans SC", sans-serif;
        font-size: 14px;
        font-weight: 400;
    }
}

