    background-image: linear-gradient(45deg, transparent 50%, #fff 50%), linear-gradient(135deg, #fff 50%, transparent 50%), linear-gradient(to right, #666 0%, #4c3b3b 3%, #4c3b3b 100%);
    background-position: calc(100% - 19px) calc(1em + 1px), calc(100% - 13px) calc(1em + 1px), 100% 0;
    background-repeat: no-repeat;
    background-size: 6px 6px, 6px 6px, 40px 100%;
    border-radius: 5px;
    border: 1px solid #666;
    display: block;
    line-height: normal;
    padding: 10px 60px 10px 15px;
    cursor: pointer;
}
.input-select:hover {
    background-image: linear-gradient(45deg, transparent 50%, #fff 50%), linear-gradient(135deg, #fff 50%, transparent 50%), linear-gradient(to right, #666 0%, #554545 3%, #554545 100%);
    background-position: calc(100% - 19px) calc(1em + 1px), calc(100% - 13px) calc(1em + 1px), 100% 0;
}
