@-moz-document domain("developer.android.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.devsite-article-body p img {
	background: #fff;
}

/* Background 
_____________________________________________________________________________________________________*/

.devsite-book-nav-bg,
.scaffold>top-nav,

body[layout=docs] .devsite-article,
body[pending] devsite-book-nav,
devsite-footer-linkboxes,
devsite-footer-promos,
devsite-footer-utility,
devsite-header .devsite-collapsible-section,
devsite-header .devsite-top-logo-row,
footer,
navigation-rail
{
	background: #060505!important;
	background-color: #060505!important;
}

devsite-tabs .devsite-tabs-wrapper
{
    background-color: #1a1a1a!important;
}


/* Background Transparent 
_____________________________________________________________________________________________________*/

.devsite-landing-row:not([background]),
.project-card:focus,
.project-card:hover,
.type ol:not([class])>li::before,

[background],
devsite-header cloudx-tabs-nav.upper-tabs .devsite-tabs-wrapper,
devsite-header devsite-tabs.upper-tabs .devsite-tabs-wrapper,
devsite-search .devsite-searchbox,
devsite-search .devsite-searchbox:before,
devsite-selector,
table.responsive td tr:not(.alt) td:first-child,
table.responsive tr:not(.alt) td td:first-child, td,
th code,
th,
tr
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.android-quick-link,
.bg-primary,

[background]:not(.devsite-landing-row-cards) .button
{	
	background: radial-gradient(circle, #031017 0%, #0a587f 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 0 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.android-quick-link::before,
.bg-primary::before,

[background]:not(.devsite-landing-row-cards) .button::before
{
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.android-quick-link:hover::before,
.bg-primary:hover::before,

[background]:not(.devsite-landing-row-cards) .button:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.button.dismiss
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 0 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.button.dismiss::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.button.dismiss:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.button-blue,
.button-green,
.button-primary,
.button-red,

body devsite-footer-utility .devsite-footer-utility-button>a
{	
	background: radial-gradient(circle, #022d01 0%, #228005 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: 0 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.button-blue::before,
.button-green::before,
.button-primary::before,
.button-red::before,

body devsite-footer-utility .devsite-footer-utility-button>a::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.button-blue:hover::before,
.button-green:hover::before,
.button-primary:hover::before,
.button-red:hover::before,

body devsite-footer-utility .devsite-footer-utility-button>a:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

navigation-tree
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.hairline,

table
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.center-margin:hover,
.devsite-landing-row-inner:hover,
.devsite-sidebar[fixed] .sidebar-content:hover,
.hairline:hover,
.justify-content-center:hover,
.project-sections:hover,

navigation-tree:hover,
table:hover
{
