devsite-tabs tab>a:focus,
devsite-tabs tab>a:hover,
devsite-tabs.upper-tabs>.devsite-tabs-wrapper>tab[active]>a
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
