style.css:
@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 url-prefix(http://krautchan.net),
 url-prefix(https://krautchan.net),
 domain("www.krautchan.net") {

div#WzTtDiV {
 display: none !important; }
 
body {
 background: #363636 !important; color: #7c7c80 !important; }

.thread_body {
 background: none !important; }

div.report_field.popup {
 background: #565658 !important; border: 1px solid #d2d2d2 !important; }

h1, form, td, .file_thread, .file_thread a, .postbody, .newsbody, .postsubject, .site_name {
 color: #fff !important; }

h2 {
 margin-bottom: 10px !important; }

.menu_title {
 margin-bottom: 10px !important; }

.menu_title, .file_thread, .postbody, h2, span.spoiler:hover {
 background: #363636 !important; }

.file_reply, .postreply, .newsbody, .postreply_highlighted, td.label, span.hidden_thread, .board_newposts, .postreply span.spoiler:hover {
 background: #565658 !important; }

.file_thread, .file_reply {
 margin: 5px 10px 20px 5px !important; padding: 5px !important; }

.file_thread {
 border: 1px dashed #565658 !important; }

.file_reply {
 border: 1px dashed #a5a5a5 !important; }

.postreply, .newsbody, table, tr, td, td.label, span.hidden_thread {
 border: none !important; }

.postreply_highlighted {
 border: 2px dashed #a5a5a5 !important; }

span.quote {
 color: #e8e7e7 !important; font-style: italic; font-size: 85%; }

span.spoiler {
 color: #161616 !important; background: #161616 !important; }

span.spoiler:hover {
 color: #c4c4c4 !important; }

span.nav_group_button {
 background: #484848 !important;
 border-color: #a7a7a7 !important;
 color: #fff !important; }

.postheader .postdate, .site_slogan {
 color: #666 !important; }

.postreply .postdate, .postreply_highlighted .postdate, .postheader a.quotelink {
 color: #7c7c80 !important; }

.postername, a, .postreply a.quotelink, a.quotelink:hover, .postreply_highlighted a.quotelink, .boardlist a {
 color: #ababab !important; }

.postform ul {
 font-size: 70% !important; }

a:hover, .boardlist a {
 text-decoration: underline !important; }

a {
 text-decoration: none !important; color: #fff !important; }

.boardlist a {
 font-size: 90% !important;}

.boardlist a:hover {
 background: #666 !important; color: #fff !important; }


