style.css:
@-moz-document url-prefix("https://10fastfingers.com") {
:root {
	--background-default: #161616;
	--primarycolor: rgba(134, 255, 5, 1);
	--secondarycolor: red;
	--textcolor: rgba(134, 240, 10, .7);
	cursor: url(https://cur.cursors-4u.net/others/oth-6/oth551.cur), auto !important;
	
}


body{
	background: var(--background-default) !important;
	color: var(--textcolor) !important;
	
}

::-webkit-scrollbar {
  display: none;
}

div{
	background: var(--background-default);
	color: var(--textcolor) !important;
	border-color: var(--primarycolor) !important;
}

div .row{
	background: var(--background-default) !important;
	color: var(--textcolor);
	padding: 5px;
	text-align: left;
	
}

.main-layout #main-content, .main-layout .main-content {
	background: var(--background-default) !important;
	
}

p{
	color: var(--primarycolor) !important;
	background: var(--background-default) !important;
}

#tagline {
	color: var(--primarycolor) !important;
	text-align: center;
}

a.navbar-brand {
	background-image: url('https://img.10fastfingers.com/img/layout/logo@2x.png') !important;
	background-size: 123px 39px !important;
}

.span {
	background: var(--background-default);
	color: var(--textcolor);
}
	
#speedtest-main #row1 {
	background: var(--background-default);
}

#speedtest-main .correct {
	color: green !important;
}


.top-first-bg {
	background: var(--background-default) !important;
	border: none;
}

a {
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
	background: var(--background-default);
	color: var(--primarycolor) !important;
}
a:hover {
	background: var(--background-default) !important;
	color: var(--secondarycolor) !important;
}

#speedtest-main #row1 span {
	border-radius: 8px;
}

#homepage #main #centerbox a.cta_button {
	color: var(--background-default) !important;
}

#homepage #main #centerbox a.cta_button:hover {
	color: var(--secondarycolor) !important;
}

#homepage #sidebar {
	background-color: var(--background-default) !important;
}

#homepage #sidebar #login-register, #homepage #sidebar #language {
	background-color: var(--background-default) !important;
	border: 2px solid var(--primarycolor) !important;
	border-radius: 10px;
	font-weight: 550;
	text-decoration: none;
}


#speedtest-main .highlight {
	background: rgba(134, 255, 5, .7) !important;
}

#speedtest-main #input-row #inputfield {
	background: var(--background-default) !important;
	text-align: center;
	border: 2px solid var(--primarycolor);
	margin-top: 10px;
	margin-bottom: 7px !important;
}

#footer-bg {
	background: var(--background-default) !important;
	border: none important;
	
}

.btn-primary{
	background: rgba(134, 255, 5, .9) !important;
	border-color: var(--primarycolor) !important;
	color: var(--background-default) !important;

}

#competition-url {
	background: var(--background-default) !important;
}

#competition-url #share-link {
	background: var(--background-default) !important;
}

#competition-url #share-stuff a.share-btn:hover {

}

#speedtest-main #reload-btn {
	height: 40px;
	width: 60px;	
	text-align: center;
}

#speedtest-main #words {
	text-align: center;
	height: 210px;
	border-radius: 15px  !important;
}

#row1 {
	color: var(--primarycolor) !important;
}

.list-group-item {
	background: var(--background-default) !important;
	
}

.main-layout #sidebar-right {
	color: var(--primarycolor) !important;
	border: none;
}


a.list-group-item.active, a.list-group-item.active:hover, a.list-group-item.active:focus {
	border: 5px solid var(--primarycolor);
	border-radius: 10px;
	margin-bottom: 5px;
	margin-top: 5px;


}

.list-group-item {
	border: 1px solid var(--primarycolor);

}

.main-layout {
	
	display: flex;
	flex-direction: column;
	justify-content: center;

}

#badge-box {
	background: var(--background-default) !important;
}

#badge-box label input{
	background: green;
}

input, button, select, textarea {
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
	background: var(--background-default);
	color: var(--textcolor) !important;

}



#input-row #inputfield {
	background: var(--background-default) !important;
	border: 2px solid var(--primarycolor) !important;
	text-align: center !important;
	border-radius: 30px;
	color: var(--primarycolor) !important;
}


#auswertung-result {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	border: 4px solid var(--primarycolor) !important;
	box-shadow: none;
}

#auswertung-result h3 {
	font-size: 20px;
	background: var(--background-default);
	border-bottom: 2px solid var(--primarycolor);
	text-align: center;
	color: var(--primarycolor);
	margin-bottom: 3px;
}

#auswertung-result #result-table td#wpm strong {
	background: var(--background-default) !important;
	font-size: 3.5em;
	
}

#auswertung-result #result-table td.name {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	
}

#auswertung-result #result-table td {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	

}

#auswertung-result #result-table tr#wrong .value {
	color: var(--secondarycolor) !important;
	
}

.correct {
	color: green;
}

#auswertung-result #result-table td.value {
	font-size: 18px;
}


button, html input[type="button"], input[type="reset"], input[type="submit"] {
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
	background: var(--primarycolor) !important;
	color: var(--background-default) !important;
	text-align: center;
}


div .row {
	align-items: center;
}

.highscore-box {
	width: 70em;
}

.form-control {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	border: 1px solid var(--primarycolor);
	box-shadow: none;
}

#badge-box h3#box-title {
	background: var(--primarycolor) !important;
	color: var(--background-default) !important;
	font-size: 20px;
	text-align: center;
	margin-top: 30px;
	border-radius: 10px;
}

div a.achievement .bg-img{
}


/*for login page*/
.nav-tabs {

	border-bottom: none;
	text-align: center;
	font-size: 20px;
}
.nav-tabs>li>a {
	background: rgba(160, 237, 78, .5) !important;
	color: var(--background-default) !important;
	margin: 10px;
	font-weight: 550;
	border-radius: 15px;
	border: none;
}
.nav-tabs>li.active>a {
	background: rgba(160, 237, 78, .8) !important;
	color: var(--background-default) !important;
	font-weight: 550;
	border-radius: 15px;
	border: none;
}

.nav-tabs>li>a:hover {
	background: var(--primarycolor) !important;
	color: var(--background-default) !important;
	font-weight: 600;
	border-radius: 10px;
	border: none;
}

hr {
	border-top: none;
}
.main-layout #main-content h2, .main-layout .main-content h2, .main-layout #main-content h3, .main-layout .main-content h3{
	border-bottom: none;
	border: 2px solid var(--primarycolor);
	border-radius: 10px;
	text-align: center;
	padding: 5px;
	background: var(--primarycolor);
	color: var(--background-default);
	font-weight: 600;
}

label {
	color: var(--primarycolor);
}

#timer {
	background: var(--background-default) !important;
	color: var(--secondarycolor) !important;
	font-size: 2em;
}

.content-box h3, .content-box h4, .content-box h5 {
	border-bottom: 2px solid var(--primarycolor) !important;
}

table#join-competition-table td {
	background-color: var(--background-default) !important;
	color: var(--primarycolor) !important;
	font-size: 1em;
	border-bottom: 1px solid var(--primarycolor) !important;
}

#competition-lang-selector label.lang {
	border: 1px solid var(--primarycolor);
	border-radius: 10px;
	margin: 7px;
}

#competition-lang-selector label.lang:hover {
	background: var(--primarycolor);
	color: var(--secondarycolor);
	
}
#competition-lang-selector label.lang.active{
	background: var(--primarycolor) !important;
	font-size: 1.3em;
	color: var(--background-default) !important;
}

#words {
	
	text-align: center !important;
	height: 210px !important;
	border: 3px solid var(--primarycolor) !important;	
	border-radius: 15px !important;
}

#reload-btn button {
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
	border-radius: 10px;
	border-color: var(--background-default) !important;
}

#words .highlight {
	background: rgba(134, 255, 4, .8) !important;
	border-radius: 10px !important;
	
}

#input-row {
	background: var(--background-default) !important;
	
}

.main-layout #sidebar-right {
	background: var(--background-default) !important;
}

#main-content-trenner {
	background: var(--background-default) !important;
}

#content-bg {
	background: var(--background-default) !important;
}


#auswertung-result #result-table td {
	border-color: var(--primarycolor) !important;
}

#competition-rank-table tbody td {
	background: var(--background-default) !important;
	border-color: var(--primarycolor) !important;
}


.tab-content {
	background: var(--background-default) !important;
	
}


.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
	background: var(--background-default) !important;
	border-color: var(--primarycolor) !important;
}
.table-striped>tbody>tr:nth-child(even)>td, .table-striped>tbody>tr:nth-child(even)>th {
	background: var(--background-default) !important;
	border-color: var(--primarycolor) !important;
}

.table>thead>tr>th {
	border-bottom: none;
	border-color: var(--background-default) !important;
}

.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
	border-color: var(--background-default) !important;
	
}


.highscore-box #table-data {
	background: var(--background-default) !important;
}


#profile-description {
	color: var(--primarycolor) !important;
	font-size: 1.3em;
	font-weight: 600;
}

@media only screen and (max-width: 1200px) {
  .highscore-box {
    width: 50em;
  }
}

@media only screen and (max-width: 800px) {
  .highscore-box {
    width: 30em;
	border: none;
  }
}

.alert {
	background: var(--background-default) !important;
	border-radius: 10px;
}
.alert-danger {
	background: var(--background-default) !important;
}

.second-row span#notification-minibox {
	background: var(--background-default) !important;
	
}
.second-row span#user-minibox {
	background: var(--background-default) !important;
}

table#profile-data-table td:nth-child(2) {
	color: var(--textcolor) !important;
}

table#profile-data-table td:nth-child(1) {
	color: var(--primarycolor) !important;
}

.btn-default {
	background: var(--primarycolor) !important;
	color: var(--background-default) !important;
	font-weight: 500;
	border-radius: 7px;
}

.btn-sm {
	background: transparent !important;
	color: var(--background-default) !important;
	font-size: .7em;
	font-style: italic;
	font-weight: 550;
	border: none;	
}

.btn-sm:hover{
	background: var(--background-default) !important;
	color: var(--secondarycolor) !important;
	font-size: .7em;
	border: none;	
}

#text-practice .workspace {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	border-radius: 20px;
	border: 3px solid var(--primarycolor) !important;
	height: 210px;
	font-size: 1.9em;
	padding: 20px;
	text-align: center;

}

#text-practice .workspace span.highlight {
	background-color: var(--primarycolor) !important;
	color: var(--background-default) !important;
	border-radius: 5px;
	padding: 5px;
}

#text-practice #text_typed {
	text-align: center;
	box-shadow: none;
	border: 2px solid var(--primarycolor) !important;
	border-radius: 20px;
}

#text-practice .workspace span.current {
	position: none;
	display: none;
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	border: 2px solid red;
	padding: 2px;
	margin: 2px;
	border-radius: 7px;
}

#top1000-index-container #softcore-mode .levels a.level, #top1000-index-container #hardcore-mode .levels a.level{
	background: var(--background-default) !important;
	
}
#top1000-view #speed-tracker .speed-marker{
	font-size: 1em;
	
}
#top1000-view #speed-tracker #speed-meter {
	background: var(--background-default) !important;

}

#top1000-view #speed-tracker #speed-meter > #inner {
	background: green;
}

#top1000-view .highlight-wrong{
	border-radius: 10px !important;
}

#top1000-view #words {
	border-radius: 15px;
	margin-bottom: 15px;
}

#top1000-auswertung {
	background: var(--background-default) !important;
}

.input-group .form-control:first-child, .input-group-addon:first-child, .input-group-btn:first-child>.btn, .input-group-btn:first-child>.dropdown-toggle, .input-group-btn:last-child>.btn:not(:last-child):not(.dropdown-toggle){
	background: var(--background-default) !important;
	border-radius: 7px;
}

#text-practice .workspace span.highlight-wrong {
	border-radius: 7px !important;
	padding: 5px;
}

.btn-group>.btn, .btn-group-vertical>.btn {
	border-radius: 10px;
}

pre {
	background: var(--background-default) !important;
	color: var(--textcolor) !important;
	border: 2px solid var(--primarycolor) !important;
	border-radius: 20px;
}

#profile-picture img {
	background: var(--background-default) !important;
	border: 5px solid var(--primarycolor) !important;

	border-radius: 50%;
}

#language-list {
	background: var(--background-default) !important;
	border: 3px solid var(--primarycolor) !important;
	border-radius: 20px;
}

#language-list h4 {
	background: var(--background-default) !important;
	text-align: center;
	font-weight: 600;
	color: var(--primarycolor) !important;
}

.btn-success {
	background: var(--primarycolor) !important;
	color: var(--background-default) !important;
	
}

.wrap{
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
}

#footer-bg #founded {
	background: var(--background-default) !important;
	color: var(--primarycolor) !important;
	text-align: center;
}

.content-box {
	background: var(--background-default) !important;
}

.highlight-wrong {
	border-radius: 7px !important;
}

#settings {
	background: var(--background-default) !important;
}

#page {
	background: var(--background-default) !important;
}

#page h2, #page h3 {
	border-bottom-color: var(--textcolor) !important;
}

.nav>li>a:hover, .nav>li>a:focus {
	background: var(--background-default);
}

.panel-primary>.panel-heading {
	background: var(--background-default) !important;
}

#participation {
	background: var(--background-default) !important;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
	color: var(--background-default) !important;
	margin-bottom: 10px;
}
}

