style.css:
@-moz-document url("https://abema.tv/"), url("https://abema.tv/timetable"), url-prefix("https://abema.tv/video"), url-prefix("https://abema.tv/now-on-air") {
@namespace url(http://www.w3.org/1999/xhtml);
/* preview video mouse over de kakudai shinai */
.com-TvAreaOfPreviewLayout__overview:hover+.com-TvAreaOfPreviewLayout__preview {
    transform: initial !important;
}



/* thumbnail hisentaku channel akarusa */
.com-TvAreaOfPreviewLayout .com-home-ChannelCardCarousel__ChannelCard:not(.com-home-ChannelCardCarousel__ChannelCard--displaying) .com-home-TvAreaCardButton__content {
    background: rgba(0,0,0,0.2) !important;
}

/* thumbnail sentaku channel akarusa */
.com-TvAreaOfPreviewLayout .com-home-ChannelCardCarousel__ChannelCard.com-home-ChannelCardCarousel__ChannelCard--displaying .com-home-TvAreaCardButton__content {
    background: rgba(0,0,0,0) !important;
}

/* channel logo size */
.com-TvAreaOfPreviewLayout .com-home-TvAreaCardButton__logo {
    width: 40% !important;
}

/* channel logo shadow */
.com-TvAreaOfPreviewLayout .com-home-TvAreaCardButton__logo {
    opacity: 1 !important;
    transition: initial !important;
    background: rgba(0,0,0,0.5) !important;
}

/* channel logo position */
/* shita center */
.com-TvAreaOfPreviewLayout .com-home-TvAreaCardButton__content {
    justify-content: flex-end !important;
    flex-direction: column  !important;
}

/* preview kashiiki bimyouni kakudai */
.com-TvAreaOfPreviewLayout__content {
    grid-template-rows: minmax(0,1fr) 62px minmax(130px,auto) !important;
}

.com-home-ChannelList {
    display: grid !important;
    grid-template-rows: 62px auto;
}

.com-home-ChannelList__reorder-button-under-carousel {
    grid-row: 1/2;
    margin: 0 0 18px 0 !important;
    justify-content: flex-start !important;
}

.com-home-TvAreaOfPreviewLayout__tv-area-layout-switcher-wrapper {
    align-items: initial !important;
    height: 62px !important;
    z-index: 3 !important;
}

.com-TvAreaOfPreviewLayout__channel-list {
    grid-row: 2/4 !important;
}

/* search form shadow noudo */
.com-application-Header .com-search-SearchForm .com-InputText__input:not(:focus) {
    background-color: rgba(0,0,0,0.4) !important;
}
.com-application-Header .com-search-SearchForm .com-InputText__input:not(:placeholder-shown) {
    background-color: #fff !important;
}/* utsukushiku nai style */

/* preview ichiran switch shadow noudo */
.com-TvAreaOfPreviewLayout .com-home-TvAreaLayoutSwitcher__inner {
        background-color: rgba(0,0,0,0.4) !important;
}
}

