style.css:
@-moz-document url-prefix("https://sts.edu.turku.fi/adfs") {
#branding {display: none !important;}
#brandingWrapper {background-color: rgba(0, 0, 0, 0) !important;}
#fullPage {background: url(https://i.imgur.com/qlbnFNd.png) center center no-repeat transparent !important; background-size: cover !important;}
#contentWrapper {background-color: rgba(0, 0, 0, 0.31)!important;max-width: 540px; height: min-content !important; overflow: hidden;margin: 0 auto;border: 1px solid rgba(0,81,128,.05);-webkit-box-shadow: 0 1px 1px rgba(0,81,128,.08);box-shadow: 0 1px 1px rgba(0,81,128,.08);top: 10%;left: 10%; right: 10%;position: relative;display: grid;position: fixed !important;color: dimgrey}
input {background-color: rgba(0, 0, 0, 0.31)!important;color: #00b3a1 !important;}
input, input:focus {border:1px solid rgba(0, 0, 0, 0.62) !important}
.submit {background-color: #51666c !important;background: #51666c !important;}
.submit:hover {background-color: #00b3a1 !important;background: #00b3a1 !important;}
}

@-moz-document url-prefix("https://edu.turku.fi") {
*:not(button) {color:#026892 !important}
#main, #page, #masthead {background: transparent !important;}
.footer-widgets-wrapper, .footer-socket-wrapper, #header-text-nav-container {display: none !important;}
html *::-webkit-scrollbar {display: none;}
html * {scrollbar-width: none !important; -ms-overflow-style: none;}
html {background: url(https://i.imgur.com/qlbnFNd.png) center center no-repeat transparent !important; background-size: cover !important;width: 100% !important; height: 100% !important;}
body {background-image: none !important;background: transparent !important;}
#primary, .portaalikuvakkeet a, #secondary, .header-post-title-container, .entry-meta {background-color: rgba(0, 0, 0, 0.31)!important;border: 1px solid rgba(0, 0, 0 ,0.31) !important;}
hr {background-color: rgba(0, 0, 0, 0.31)!important;}
.widget-title span, #secondary .widget ul li {border-bottom: none !important;}
#secondary .widget ul li {border-bottom: 1px solid rgba(0, 0, 0, 0.31);}
#primary, #secondary {padding: 20px;}
#primary h2, #secondary h3 {color: #026892  !important;}
input {background-color: rgba(0, 0, 0, 0.31)!important;color: white !important;margin-left: 10px !important;}
input, input:focus {border:1px solid rgba(0, 0, 0, 0.62) !important}
#page {box-shadow: initial !important;-webkit-box-shadow: initial !important;}
.portaalikuvakkeet a:nth-child(6) {background-image: url('https://edu.turku.fi/wp-content/uploads/2017/04/logo-moodle-oranssi.png') !important;}
.portaalikuvakkeet a:nth-child(7) {background-image: url('https://edu.turku.fi/wp-content/uploads/2017/04/logo-moodle-sininen.png') !important;}
.portaalikuvakkeet a:nth-child(8) {background-image: url('https://edu.turku.fi/wp-content/uploads/2017/04/logo-moodle-punainen.png') !important;}
.portaalikuvakkeet a:nth-child(9) {background-image: url('https://edu.turku.fi/wp-content/uploads/2017/04/logo-moodle-vihrea.png') !important;}
.specificfeedback {color: #be3636;}
}

@-moz-document url-prefix("https://taito.edu.turku.fi") {
html {background: transparent url(https://i.imgur.com/qlbnFNd.png) center center/cover no-repeat fixed !important;width: 100% !important; height: 100% !important;}
#page-footer, .pace, .nav-item.mr-2:first-child {display: none !important;}
body, #main, #page, .content, #dockeditempanel, .generalbox, .dockeditempanel_hd, .coursebox.panel.hover .panel-body:hover, .cimbox, .activity-wrapper, .activity_footer, .nav_icon, .cell, .fp-thumbnail, .fp-filename, .block, .bg-white, div[role=main] *:not(.btn):not(.form-control):not(.custom-select):not(.filemanager):not(input):not(.progress-bar):not(.dropdown-menu):not(.dropdown-item):not(.coursebox):not(.activity-date-submitted):not([id^="section"]), .fp-iconview .fp-thumbnail img, #main-navbar {background-color: transparent !important;border: initial !important;}

#adaptable-page-header-wrapper, #inst20, #region-main, #navbarSupportedContent, .custom-select, .breadcrumb, .form-control, .courses .coursebox, .block .header, .card, .filemanager div:nth-child(4) > div > div, .moodle-dialogue-base .moodle-dialogue-wrap,.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd, .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd, .btn, .btn-login, #page-login-index .card, .list-group-item, .btn.coursemenubtn:hover, .filemanager, input, .popover-region-header-container, .popover-region-footer-container, .nav-item.mr-2, .nav-pills > .active > a:hover, .nav-pills > .active > a, .panel.hover .coursebtn.submit.btn {background-color: rgba(0, 0, 0, 0.31)!important;border: 1px solid rgba(0, 0, 0 ,0.31) !important;}

.moodle-dialogue {background: url(https://i.imgur.com/qlbnFNd.png) center center no-repeat transparent !important;background-size: cover !important;height: initial !important;}

.fp-file:hover .fp-filename.text-truncate{text-align: center !important;padding: 5px !important;border: 1px solid rgba(0,0,0,0.31) !important;}
.dropdown-menu:hover, .popover-body, .popover-region-container, .message-app, .fp-file:hover .fp-filename.text-truncate {background-color: #222 !important;}
.dropdown-menu a:hover, .dropdown:hover {background-color: #0abdab !important;}
.dropdown-item.active {background-color: inherit !important}

.file-picker .fp-content {height: 430px;}
.filepicker .moodle-dialogue-bd {max-height:initial}

#adaptable-page-header-wrapper {background: rgba(0, 0, 0, 0.31) 10px 10px no-repeat !important;height: 100px !important;}
.nav-link, .dropdown-menu a, .menutitle, .menutitle:hover, .cell, html, body, label, span, h1, h2, h3, h4, h5, h6 ,p, .dropdown-toggle::after, .fa-ellipsis-h::before, .branch::before, .breadcrumb li span, #usermenu span, .tree_item span, .tree_item a, .tree_item p, .tree_item i, a.nav-link:hover, .fp-repo-area.nav-pills a *,.c0 ,.c1, .prompt {color: #e6e6e6 !important;}
.dropdown-menu a:hover, .dropdown-item:hover, .show a span, .show:hover a span {color: #222 !important;}


div > .navbar-nav {position: relative !important;top: -28px !important;}
nav {position: relative !important;top: 0px;}
#main-navbar {position: relative;top: -13px;}

.border-bottom, .dropdown-menu {border-bottom: 1px solid rgba(0, 0, 0 ,0.31) !important;color: #e6e6e6 !important;}
.btn.coursemenubtn {border-radius: 100px !important;}

.panel.hover .coursebtn.submit.btn:hover {background-color: #0abdab !important;border: 1px solid rgba(0, 0, 0 ,0.31) !important;}
.coursebtn.submit.btn:hover span {color: #e6e6e6 !important;}
.coursebox.hover:hover .summary, .coursebox.hover .summary{word-wrap: break-word;width: 100%}
.cimbox {background-image: initial !important;}

a:hover, item-tree *, #sitetitle h1,  a *:not(i), * a, .breadcrumb i, .tree_item span:hover, .coursebox.panel.hover .panel-body:hover .coursebox-content h3 a {color: #0abdab !important;}
.btn:hover, .btn-login:hover, a.btn *:hover, a.btn[title="Kirjaudu @edu.turku.fi-tunnuksella!"]:hover, .progress-bar, .message.clickable {background-color: #0abdab !important;color: #fff !important;}
.btn[title="Kirjaudu @edu.turku.fi-tunnuksella"] {position: relative;left: -120%;width: 220%;}

#page-content {margin: 50px auto 25px;}
.coursebox {max-width: 30%;margin: 20px auto;}
.dropdown-menu {background-color: rgba(0, 0, 0, 0.31)!important;}
#sitetitle h1 {margin: 10px -12px 0 !important;}
#main-navigation0 {margin-right: 0 !important;}

img[src^="https://taito.edu.turku.fi/theme/image.php"] {filter: invert(90%) hue-rotate(180deg);}
.nav-item.mr-2, .nav-pills > .active > a, ul li.section.main {margin:5px; padding: 5px !important;}

.popover-region-container {z-index: 101;border: 1px solid rgba(0, 0, 0, 0.31);}
.coursebox.hover .summary {display:block;}

ul {list-style-position: inside;}
pre {color: #86bbd0 !important;}
.navbar nav {padding-left: 20px !important;}

#block-region-side-post {padding-right: 0 !important;}
}
@-moz-document url("https://taito.edu.turku.fi/user/profile.php"), url("https://taito.edu.turku.fi/"), url("https://taito.edu.turku.fi/#skipmycourses") {
#main-navbar {top: -68px !important;}
div > .navbar-nav:nth-child(2) {position: relative !important;top: 0px !important;}
}

@-moz-document url-prefix("https://taito.edu.turku.fi/course/view") {
#region-main {background-color: transparent !important;border: initial !important;padding: 0 !important;}
#page .course-content ul li.section.main  {background-color: rgba(0, 0, 0, 0.31)!important;border: 1px solid rgba(0, 0, 0 ,0.31) !important;}
}

@-moz-document url("https://taito.edu.turku.fi/my/index.php"), url-prefix("https://taito.edu.turku.fi/message/index.php") {
#main-navbar {top: -73px !important;}
div > .navbar-nav:nth-child(2) {position: relative !important;top: 0px !important;}
div[data-region="message-index"] {max-width: 90%;padding: 20px;}
div[data-region="panel-body-container"] {max-width: 90%;position: relative;left: 20px;}
}
