.cradle_ball {
    padding: 1rem;
    width: 1rem;
    float: 0;
    position: relative;
    display: inline-block;;
    animation: cradle_left none;
    animation: cradle_new 1s ease-in-out infinite;
}	
.cradle_ball:after {
    left: 0.5rem;
    top: 0rem;
    width:  2rem;
    height: 2rem
}
.reflect_crdle {
    clear:none;
    padding:1rem; 
	float:none;
}
.reflect_crdle * {
    animation: none; /* Overwrite */
    padding: 0;
    margin: 0rem 1.875rem;
    height: 0.5rem !important;
    width: 1rem !important;
    filter: blur(.5rem) !important;
    animation: cradle_left none;
    animation: cradle_new 1s ease-in-out infinite;
}
/* animaatio */
.ballleftrot, .ballrightrot {
    animation: none; /* Overwrite */
}
.cradle_cnt .cradle_ball {
    animation: none;
    animation: cradle_new 1s ease-in-out infinite;
}
.cradle_cnt :nth-child(1) {
    animation-delay: -0.5s;
}
.cradle_cnt :nth-child(2) {
    animation-delay: -0.4s;
}
.cradle_cnt :nth-child(3) {
    animation-delay: -0.3s;
}
.cradle_cnt :nth-child(4) {
    animation-delay: -0.2s;
}
.cradle_cnt :nth-child(5) {
    animation-delay: -0.1s;
}
.cradle_ball:nth-child(1)::after, .reflect_crdle > div:nth-child(1) {
    background: hsl(var(--pri-hue),100%,50%) !important;
}
.cradle_ball:nth-child(2)::after, .reflect_crdle > div:nth-child(2) {
    background: hsl(var(--sec-hue),100%,50%) !important;
}
.cradle_ball:nth-child(3)::after, .reflect_crdle > div:nth-child(3) {
    background: hsl(var(--pri-hue),100%,50%) !important;
}
.cradle_ball:nth-child(4)::after, .reflect_crdle > div:nth-child(4) {
    background: hsl(var(--sec-hue),100%,50%) !important;
}
.cradle_ball:nth-child(5)::after, .reflect_crdle > div:nth-child(5) {
    background: hsl(var(--pri-hue),100%,50%) !important;
}

